pragma solidity >=0.8.7;

import "./LoanAuctionEnums.sol";

interface LoanAuctionEventsAndErrors{
    error InvalidStepDownConfig();
    error StepDownConfigOverflow();
    error IncompleteStepDownConfig();
    error StepDownConfigAndAuctionTimeMismatch();
    
    error CollateralOwnershipNotTransferred();
    error AuctionInvalidStatus(uint256 auctionId, AuctionStatus status);
    error AuctionNotRegistered();
    error ProtectionNotOver();
    error ProtectionOver();
    error InvalidPriceRange(uint256 startAmount, uint256 endAmount);
    error InvalidTimeRange(uint256 startTime, uint256 endTime);
    error NotAuthorized();

    event LoanAuctionBegun(uint256 indexed auctionId, uint256 loanId, 
        address tokenAddr, address paymentToken, 
        address loanBorrower, uint256 tokenId, uint256 startAmount, uint256 endAmount,
        uint256 startTime, uint256 endTime, uint256 protectedUntil, uint256 loanRepaymentAmount,
        bytes stepDownConfig);
    event LoanAuctionSoldAndProtected(uint256 indexed auctionId, address bidder, address borrower, uint256 bidAmount);
    event LoanAuctionSoldToBidder(uint256 indexed auctionId, address bidder, uint256 bidAmount);
    event LoanAuctionCancelledByPayout(uint256 indexed auctionId, address borrower, uint256 payout);
}","keccak256":"0x8c5757fb16cd612b72114303bd0ae5401d524d5f77d01a77718f126ac0c3815d","license":"MIT"},"contracts/loan/LoanAuctionStructs.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.7;

import "./LoanAuctionEnums.sol";

struct AuctionParameters {
    uint256 loanId;
    address tokenAddress;
    address paymentToken;
    uint256 tokenId;
    uint256 startAmount;
    uint256 endAmount;
    uint256 startTime;
    uint256 endTime;
    bytes stepDownConfig;
    address loanBorrower;
    uint256 loanAmount;
    uint256 loanRepaymentAmount;
    address lender;
    bool isProtected;
}


struct PaymentRecipients {
    address recipient;
    uint256 amount;
    RecipientType recipientType;
}

struct Auction {
    uint256 id;
    uint256 protectedUntil;
    AuctionParameters parameters;
}","keccak256":"0x0593c2a7a4f3cd6ccfb9f508f9b64b88c1b7bcac30df95e70c31aeb20ea1cd31","license":"MIT"},"contracts/loan/LoanRepaymentCalculator.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.7;

import "../PeriFiAdmin.sol";
import "../vault/LendingPool.sol";

enum DestinationType {
    Pool,
    Lender,
    InsuranceVault,
    Commission,
    CurationRewardPool
}

struct Repayment {
    address targetAddress;
    uint256 value;
    bool guaranteed;
}


contract LoanRepaymentCalculator {
    address public insuranceVaultAddress;
    address public lendingPoolAddress;
    address public masterWalletAddress;
    address public periFiAdminAddress;
    address public curationRewardPoolAddress;

    constructor(
        address _insuranceVaultAddress,
        address _lendingPoolAddress,
        address _masterWalletAddress,
        address _periFiAdminAddress,
        address _curationRewardPoolAddress) {
        insuranceVaultAddress = _insuranceVaultAddress;
        lendingPoolAddress = _lendingPoolAddress;
        masterWalletAddress = _masterWalletAddress;
        periFiAdminAddress = _periFiAdminAddress;
        curationRewardPoolAddress = _curationRewardPoolAddress;
    }

    function getDestinations(
        uint256 loanId,
        uint256 repaymentAmount, 
        uint256 loanAmount,
        address lender
    ) external view returns (Repayment[] memory results) {
        uint256 interestDue = repaymentAmount - loanAmount;

        LendingPool lendingPool = LendingPool(payable(lendingPoolAddress));
        uint256 leverageAmount = lendingPool.loanToLeverage(loanId);

        // if there is no interest
        if (interestDue == 0) {
            if (leverageAmount == 0){
                results = new Repayment[](1);
                results[0] = Repayment(lender, loanAmount, false);
            } else {
                results = new Repayment[](2);
                results[0] = Repayment(lendingPoolAddress, leverageAmount, true);
                results[1] = Repayment(lender, loanAmount - leverageAmount, false);
            }
            return results;
        }

        uint256 interestForPool = interestDue * leverageAmount / loanAmount;
        uint256 interestForLender = interestDue - interestForPool;
        uint256 loanAmountByLender = loanAmount - leverageAmount;

        uint256 lenderForIFL;
        uint256 commissionForIFL;
        uint256 curationRewardForIFL;

        (
            lenderForIFL, 
            commissionForIFL, 
            curationRewardForIFL
        ) = distributeIFL(interestForLender);

        if (leverageAmount == 0) {
            // no leverage Used, only need to get InterestForLender (IFL)
            results = new Repayment[](3);
            results[0] = Repayment(lender, loanAmountByLender + lenderForIFL, false);
            results[1] = Repayment(masterWalletAddress, commissionForIFL, false);
            results[2] = Repayment(curationRewardPoolAddress, curationRewardForIFL, true);
        } else {
            PeriFiAdmin periFiAdmin = PeriFiAdmin(periFiAdminAddress);

            uint256 poolUsageInBasisPoints = lendingPool.loanToPoolUsageInBasisPoint(loanId);
            uint256 ivCommissionForIFP = periFiAdmin.ivCommissionForIFP();

            uint256 commissionBasisPoint = poolCommissionInBasisPointByPoolUsage(poolUsageInBasisPoints);
            
            uint256 poolForIFP = interestForPool * commissionBasisPoint / 10000;
            uint256 insuranceVaultForIFP = interestForPool * ivCommissionForIFP / 10000;
            uint256 lenderForIFP = interestForPool - (poolForIFP + insuranceVaultForIFP);

            results = new Repayment[](7);
            results[0] = Repayment(lendingPoolAddress, leverageAmount, true);
            results[1] = Repayment(lender, loanAmountByLender, false);
            results[2] = Repayment(lendingPoolAddress, poolForIFP, false);
            results[3] = Repayment(lender, lenderForIFP + lenderForIFL, false);
            results[4] = Repayment(curationRewardPoolAddress, curationRewardForIFL, true);
            results[5] = Repayment(insuranceVaultAddress, insuranceVaultForIFP, false);
            results[6] = Repayment(masterWalletAddress, commissionForIFL, false);
        }
    }

    function distributeIFL(
        uint256 interestForLender) internal view returns (
            uint256 lenderForIFL,
            uint256 commissionForIFL,
            uint256 curationRewardForIFL
        ) {
            PeriFiAdmin periFiAdmin = PeriFiAdmin(periFiAdminAddress);
            uint256 commissionInBasisPoints = periFiAdmin.commissionForIFL();
            if (commissionInBasisPoints < 100) {
                commissionInBasisPoints = 100; // to ensure curation reward commission
            }
            // no leverage Used, only need to get InterestForLender (IFL)
            uint256 totalCommission = interestForLender * commissionInBasisPoints / 10000;
            curationRewardForIFL = interestForLender / 100; // take 1%
            commissionForIFL = totalCommission - curationRewardForIFL;
            lenderForIFL = interestForLender - totalCommission;
        }

    function poolCommissionInBasisPointByPoolUsage(uint256 poolUsageInBasisPoints) internal view returns (uint256 basisPoint) {
            PeriFiAdmin periFiAdmin = PeriFiAdmin(periFiAdminAddress);
            if (poolUsageInBasisPoints <= 2000) {
                basisPoint = periFiAdmin.poolCommissionIFPUpTo20();
            }
            else if (poolUsageInBasisPoints <= 3000) {
                basisPoint = periFiAdmin.poolCommissionIFPUpTo30();
            }
            else if (poolUsageInBasisPoints <= 4000) {
                basisPoint = periFiAdmin.poolCommissionIFPUpTo40();
            }
            else if (poolUsageInBasisPoints <= 5000) {
                basisPoint = periFiAdmin.poolCommissionIFPUpTo50();
            }
            else if (poolUsageInBasisPoints <= 6000) {
                basisPoint = periFiAdmin.poolCommissionIFPUpTo60();
            }
            else if (poolUsageInBasisPoints <= 7000) {
                basisPoint = periFiAdmin.poolCommissionIFPUpTo70();
            }
            else if (poolUsageInBasisPoints <= 8000) {
                basisPoint = periFiAdmin.poolCommissionIFPUpTo80();
            }
            else if (poolUsageInBasisPoints <= 9000) {
                basisPoint = periFiAdmin.poolCommissionIFPUpTo90();
            }
            else {
                basisPoint = periFiAdmin.poolCommissionIFPUpTo100();
            }
            return basisPoint;
    }

}","keccak256":"0xeb2182c99a31800a820c1fb530c5aa807c7e17704c9a920802facd2ac80ef799","license":"MIT"},"contracts/vault/LendingPool.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.7;

import "./Vault.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

error NotSufficientBalanceInPool(
    uint256 loanId,
    uint256 balance,
    uint256 requested
);
error IllegalLeverageAccess(address sender, uint256 loanId, uint256 requested);
error AlreadyLeveragedLoan(uint256 loanId);

contract LendingPool is Vault, ReentrancyGuardUpgradeable {
    address public loanAddress;

    mapping(uint256 => bool) public leveragedLoan;

    mapping(uint256 => uint256) public loanToLeverage;

    mapping(uint256 => uint256) public loanToPoolUsageInBasisPoint;

    function initialize(
        string memory name
    ) public virtual override initializer {
        __Vault_init(name);
        __MWOwnable_init();
        __ReentrancyGuard_init();
    }

    function leverageLoan(
        uint256 loanId,
        address paymentToken,
        uint256 leveragedAmount,
        address borrower
    ) external virtual nonReentrant onlyRole("spender") {
        if (leveragedLoan[loanId]) {
            revert AlreadyLeveragedLoan(loanId);
        }

        if (leveragedAmount == 0) {
            leveragedLoan[loanId] = true;
            return;
        }

        IERC20 erc20 = IERC20(paymentToken);
        uint256 balance = erc20.balanceOf(address(this));

        if (balance < leveragedAmount) {
            revert NotSufficientBalanceInPool(loanId, balance, leveragedAmount);
        }

        erc20.transfer(borrower, leveragedAmount);
        uint256 poolUsageInBasisPoint = (leveragedAmount * 10000) / balance;
        loanToLeverage[loanId] = leveragedAmount;
        loanToPoolUsageInBasisPoint[loanId] = poolUsageInBasisPoint;
        leveragedLoan[loanId] = true;
    }
}
","keccak256":"0x7951097d1c95a2f6e30e755d76ce4e835ebdb88dc04ed9a63de0920d03c0daae","license":"MIT"},"contracts/vault/Vault.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "../helpers/SignatureHelper.sol";
import "../access/SimpleRoleAccess.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

contract Vault is SimpleRoleAccess, UUPSUpgradeable {
    using SignatureHelper for bytes32;

    event Claimed(bytes _hash);

    bytes4 private ERC1155_INTERFACE_ID;
    string public name;

    mapping(bytes => bool) public claimed;
    // Balance name => token => amount
    mapping(string => mapping(address => uint256)) public namedBalances;

    function initialize(string memory _name) public virtual initializer {
        __Vault_init(_name);
        __MWOwnable_init();
    }

    function __Vault_init(
        string memory _name
    ) internal virtual onlyInitializing {
        name = _name;
        ERC1155_INTERFACE_ID = 0xd9b67a26;
    }

    function approveERC20Transfer(
        address _tokenAddress,
        address _spender,
        uint256 _amount
    ) external virtual onlyRole("spender") returns (bool) {
        IERC20 erc20 = IERC20(_tokenAddress);
        return erc20.approve(_spender, _amount);
    }

    function transferERC20(
        address _tokenAddress,
        address _to,
        uint256 _amount
    ) external virtual onlyRole("spender") returns (bool) {
        IERC20 erc20 = IERC20(_tokenAddress);
        return erc20.transfer(_to, _amount);
    }

    function setERC721ApprovalForAll(
        address _tokenAddress,
        address _operator,
        bool _approved
    ) external virtual onlyRole("spender") {
        IERC721 erc721 = IERC721(_tokenAddress);
        erc721.setApprovalForAll(_operator, _approved);
    }

    function transferERC721(
        address _tokenAddress,
        address _to,
        uint256 _tokenId
    ) external virtual onlyRole("spender") {
        IERC721 erc721 = IERC721(_tokenAddress);
        erc721.transferFrom(address(this), _to, _tokenId);
    }

    function transferERC1155(
        address _tokenAddress,
        address _to,
        uint256 _id,
        uint256 _value,
        bytes calldata _data
    ) external virtual onlyRole("spender") {
        IERC1155 erc1155 = IERC1155(_tokenAddress);
        require(
            erc1155.supportsInterface(ERC1155_INTERFACE_ID),
            "given token address doesn't support ERC1155"
        );
        erc1155.safeTransferFrom(address(this), _to, _id, _value, _data);
    }

    function transferERC1155Batch(
        address _tokenAddress,
        address _to,
        uint256[] calldata _ids,
        uint256[] calldata _values,
        bytes calldata _data
    ) external virtual onlyRole("spender") {
        IERC1155 erc1155 = IERC1155(_tokenAddress);
        require(
            erc1155.supportsInterface(ERC1155_INTERFACE_ID),
            "given token address doesn't support ERC1155"
        );
        erc1155.safeBatchTransferFrom(address(this), _to, _ids, _values, _data);
    }

    function setERC1155ApprovalForAll(
        address _tokenAddress,
        address _operator,
        bool _approved
    ) external virtual onlyRole("spender") {
        IERC1155 erc1155 = IERC1155(_tokenAddress);
        require(
            erc1155.supportsInterface(ERC1155_INTERFACE_ID),
            "given token address doesn't support ERC1155"
        );
        erc1155.setApprovalForAll(_operator, _approved);
    }

    function getNativeBalance() external view virtual returns (uint256) {
        return address(this).balance;
    }

    function transferNative(
        address payable _to,
        uint256 _amount
    ) public payable virtual onlyRole("spender") {
        (bool sent, ) = _to.call{ value: _amount }("");
        require(sent, "Failed to send Ether");
    }

    function burn(
        address _token,
        uint256 _amount
    ) public virtual onlyRole("spender") {
        ERC20Burnable(_token).burn(_amount);
    }

    function namedBalanceReceive(
        string memory _name,
        address _token,
        uint256 _amount
    ) external virtual onlyRole("spender") {
        namedBalances[_name][_token] += _amount;
    }

    function namedBalanceSpend(
        string memory _name,
        address _token,
        uint256 _amount
    ) external virtual onlyRole("spender") {
        require(
            namedBalances[_name][_token] >= _amount,
            "Insufficient balance"
        );
        namedBalances[_name][_token] -= _amount;
    }

    function getNamedBalance(
        string memory _name,
        address _token
    ) external view virtual returns (uint256) {
        uint256 balance = namedBalances[_name][_token];
        uint256 actualBalance = _token == address(0)
            ? payable(this).balance
            : IERC20(_token).balanceOf(address(this));

        return balance <= actualBalance ? balance : actualBalance;
    }

    function claim(
        bytes[] memory _hashes,
        bytes[] memory _signatures
    ) external virtual {
        require(
            _hashes.length == _signatures.length,
            "Not matching hash and signature count"
        );

        for (uint256 i = 0; i < _hashes.length; i++) {
            if (claimed[_hashes[i]]) {
                continue;
            }

            (
                address _recipientAddress,
                address _tokenAddress,
                uint256 _amount,
                uint48 _timestamp
            ) = abi.decode(_hashes[i], (address, address, uint256, uint48));

            bytes32 _messageHash = keccak256(
                abi.encodePacked(
                    _recipientAddress,
                    _tokenAddress,
                    _amount,
                    _timestamp
                )
            );

            require(
                _messageHash.recoverSigner(_signatures[i]) == owner(),
                "Invalid signature"
            );

            require(_recipientAddress == msg.sender, "Not recipient");
            require(block.timestamp >= _timestamp, "Invalid timestamp");
            require(
                IERC20(_tokenAddress).balanceOf(address(this)) >= _amount,
                "Insufficient pool balance"
            );

            claimed[_hashes[i]] = true;
            IERC20(_tokenAddress).transfer(msg.sender, _amount);
            emit Claimed(_hashes[i]);
        }
    }

    receive() external payable {}

    fallback() external payable {}

    function _authorizeUpgrade(address) internal virtual override onlyOwner {}
}
