pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "../helpers/SignatureHelper.sol";
import "../access/SimpleRoleAccess.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

contract Vault is SimpleRoleAccess, UUPSUpgradeable {
    using SignatureHelper for bytes32;

    event Claimed(bytes _hash);

    bytes4 private ERC1155_INTERFACE_ID;
    string public name;

    mapping(bytes => bool) public claimed;
    // Balance name => token => amount
    mapping(string => mapping(address => uint256)) public namedBalances;

    function initialize(string memory _name) public virtual initializer {
        __Vault_init(_name);
        __MWOwnable_init();
    }

    function __Vault_init(
        string memory _name
    ) internal virtual onlyInitializing {
        name = _name;
        ERC1155_INTERFACE_ID = 0xd9b67a26;
    }

    function approveERC20Transfer(
        address _tokenAddress,
        address _spender,
        uint256 _amount
    ) external virtual onlyRole("spender") returns (bool) {
        IERC20 erc20 = IERC20(_tokenAddress);
        return erc20.approve(_spender, _amount);
    }

    function transferERC20(
        address _tokenAddress,
        address _to,
        uint256 _amount
    ) external virtual onlyRole("spender") returns (bool) {
        IERC20 erc20 = IERC20(_tokenAddress);
        return erc20.transfer(_to, _amount);
    }

    function setERC721ApprovalForAll(
        address _tokenAddress,
        address _operator,
        bool _approved
    ) external virtual onlyRole("spender") {
        IERC721 erc721 = IERC721(_tokenAddress);
        erc721.setApprovalForAll(_operator, _approved);
    }

    function transferERC721(
        address _tokenAddress,
        address _to,
        uint256 _tokenId
    ) external virtual onlyRole("spender") {
        IERC721 erc721 = IERC721(_tokenAddress);
        erc721.transferFrom(address(this), _to, _tokenId);
    }

    function transferERC1155(
        address _tokenAddress,
        address _to,
        uint256 _id,
        uint256 _value,
        bytes calldata _data
    ) external virtual onlyRole("spender") {
        IERC1155 erc1155 = IERC1155(_tokenAddress);
        require(
            erc1155.supportsInterface(ERC1155_INTERFACE_ID),
            "given token address doesn't support ERC1155"
        );
        erc1155.safeTransferFrom(address(this), _to, _id, _value, _data);
    }

    function transferERC1155Batch(
        address _tokenAddress,
        address _to,
        uint256[] calldata _ids,
        uint256[] calldata _values,
        bytes calldata _data
    ) external virtual onlyRole("spender") {
        IERC1155 erc1155 = IERC1155(_tokenAddress);
        require(
            erc1155.supportsInterface(ERC1155_INTERFACE_ID),
            "given token address doesn't support ERC1155"
        );
        erc1155.safeBatchTransferFrom(address(this), _to, _ids, _values, _data);
    }

    function setERC1155ApprovalForAll(
        address _tokenAddress,
        address _operator,
        bool _approved
    ) external virtual onlyRole("spender") {
        IERC1155 erc1155 = IERC1155(_tokenAddress);
        require(
            erc1155.supportsInterface(ERC1155_INTERFACE_ID),
            "given token address doesn't support ERC1155"
        );
        erc1155.setApprovalForAll(_operator, _approved);
    }

    function getNativeBalance() external view virtual returns (uint256) {
        return address(this).balance;
    }

    function transferNative(
        address payable _to,
        uint256 _amount
    ) public payable virtual onlyRole("spender") {
        (bool sent, ) = _to.call{ value: _amount }("");
        require(sent, "Failed to send Ether");
    }

    function burn(
        address _token,
        uint256 _amount
    ) public virtual onlyRole("spender") {
        ERC20Burnable(_token).burn(_amount);
    }

    function namedBalanceReceive(
        string memory _name,
        address _token,
        uint256 _amount
    ) external virtual onlyRole("spender") {
        namedBalances[_name][_token] += _amount;
    }

    function namedBalanceSpend(
        string memory _name,
        address _token,
        uint256 _amount
    ) external virtual onlyRole("spender") {
        require(
            namedBalances[_name][_token] >= _amount,
            "Insufficient balance"
        );
        namedBalances[_name][_token] -= _amount;
    }

    function getNamedBalance(
        string memory _name,
        address _token
    ) external view virtual returns (uint256) {
        uint256 balance = namedBalances[_name][_token];
        uint256 actualBalance = _token == address(0)
            ? payable(this).balance
            : IERC20(_token).balanceOf(address(this));

        return balance <= actualBalance ? balance : actualBalance;
    }

    function claim(
        bytes[] memory _hashes,
        bytes[] memory _signatures
    ) external virtual {
        require(
            _hashes.length == _signatures.length,
            "Not matching hash and signature count"
        );

        for (uint256 i = 0; i < _hashes.length; i++) {
            if (claimed[_hashes[i]]) {
                continue;
            }

            (
                address _recipientAddress,
                address _tokenAddress,
                uint256 _amount,
                uint48 _timestamp
            ) = abi.decode(_hashes[i], (address, address, uint256, uint48));

            bytes32 _messageHash = keccak256(
                abi.encodePacked(
                    _recipientAddress,
                    _tokenAddress,
                    _amount,
                    _timestamp
                )
            );

            require(
                _messageHash.recoverSigner(_signatures[i]) == owner(),
                "Invalid signature"
            );

            require(_recipientAddress == msg.sender, "Not recipient");
            require(block.timestamp >= _timestamp, "Invalid timestamp");
            require(
                IERC20(_tokenAddress).balanceOf(address(this)) >= _amount,
                "Insufficient pool balance"
            );

            claimed[_hashes[i]] = true;
            IERC20(_tokenAddress).transfer(msg.sender, _amount);
            emit Claimed(_hashes[i]);
        }
    }

    receive() external payable {}

    fallback() external payable {}

    function _authorizeUpgrade(address) internal virtual override onlyOwner {}
}
","keccak256":"0x08f1faf21b97709c47f65ae9f075a5658dfd07a2fdfed06f3197369389cff958","license":"MIT"},"contracts/voting/PeriFiCuration.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "../helpers/SignatureHelper.sol";
import "../vault/Vault.sol";
import "./ScenarioOneV2.sol";
import "../access/MWOwnable.sol";
import "../token/ERC20/NEND.sol";
import "./ScenarioOneV2.sol";
import "./interfaces/ICuration.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

contract PeriFiCuration is ICuration, MWOwnable, UUPSUpgradeable {
    using SignatureHelper for bytes32;

    // Curation hash => votes
    mapping(bytes32 => Vote[]) public votes;
    mapping(bytes32 => uint48) public curationHashToEndTime;
    mapping(bytes32 => bool) public curationHashToEnded;
    address[4] public vpcAddresses;
    Vault public rewardPool;

    NEND public nend;

    function initialize(
        Vault _rewardPool,
        NEND _rewardToken,
        address[4] memory _vpcAddresses
    ) public virtual initializer {
        require(_vpcAddresses.length == 4, "Invalid number of vpc addresses");
        rewardPool = _rewardPool;
        nend = _rewardToken;
        vpcAddresses = _vpcAddresses;

        __MWOwnable_init();
    }

    function setRewardPool(Vault _rewardPool) external virtual onlyOwner {
        rewardPool = _rewardPool;
    }

    function setNend(NEND _nend) external virtual onlyOwner {
        nend = _nend;
    }

    function setVPCAddress(
        uint8 _level,
        address _address
    ) external virtual onlyOwner {
        vpcAddresses[_level - 1] = _address;
    }

    function setVPCAddresses(
        address[4] memory _addresses
    ) external virtual onlyOwner {
        vpcAddresses = _addresses;
    }

    function castVote(
        bool _accept,
        bool _bet,
        bool _leverageLending,
        uint8 _loanAmount,
        uint8 _duration,
        uint8 _apr,
        uint8 _cardLevel,
        uint256 _cardId,
        uint48 _curationEnd,
        bytes32 _curationHash,
        bytes memory _signature
    ) external virtual override {
        bytes32 messageHash = keccak256(
            abi.encodePacked(_curationEnd, msg.sender, _curationHash)
        );

        require(block.timestamp <= _curationEnd, "Curation ended");

        require(
            messageHash.recoverSigner(_signature) == owner(),
            "Invalid signature"
        );

        require(_cardLevel >= 1 && _cardLevel < 5, "Invalid card level");

        require(
            PERIv2(vpcAddresses[_cardLevel - 1]).revealChecked(_cardId),
            "Card not releaved"
        );

        if (curationHashToEndTime[_curationHash] != 0) {
            require(
                curationHashToEndTime[_curationHash] == _curationEnd,
                "Curation end mismatch"
            );
        } else {
            curationHashToEndTime[_curationHash] = _curationEnd;
        }

        for (uint256 i = 0; i < votes[_curationHash].length; i++) {
            if (votes[_curationHash][i].voter == msg.sender) {
                revert("Already voted");
            }
        }

        require(
            vpcAddresses[_cardLevel - 1] != address(0),
            "VPC address not set"
        );

        IERC721(vpcAddresses[_cardLevel - 1]).transferFrom(
            msg.sender,
            address(rewardPool),
            _cardId
        );

        uint48 voteTime = uint48(block.timestamp);

        votes[_curationHash].push(
            Vote(
                _accept,
                _bet,
                _leverageLending,
                false,
                false,
                _loanAmount,
                _duration,
                _apr,
                _cardLevel,
                _cardId,
                voteTime,
                msg.sender,
                0
            )
        );

        emit VoteCasted(
            _accept,
            _bet,
            _leverageLending,
            _loanAmount,
            _duration,
            _apr,
            _cardLevel,
            _cardId,
            voteTime,
            _curationHash,
            msg.sender
        );
    }

    function redeemVpc(bytes32 _curationHash) external virtual override {
        Vote[] storage _votes = votes[_curationHash];

        for (uint256 i = 0; i < _votes.length; i++) {
            Vote storage _vote = _votes[i];
            if (_vote.voter == msg.sender && _vote.vpcRedeemable) {
                _vote.vpcRedeemable = false;
                rewardPool.transferERC721(
                    vpcAddresses[_vote.cardLevel - 1],
                    _vote.voter,
                    _vote.cardId
                );

                emit VPCRedeemed(_curationHash, msg.sender);
                return;
            }
        }

        revert("No vote valid for vpc redeem");
    }

    function endCuration(
        EndCurationArgs memory args
    ) external virtual override onlyOwner {
        require(!curationHashToEnded[args.curationHash], "Curation ended");

        require(
            curationHashToEndTime[args.curationHash] == 0 ||
                block.timestamp >= curationHashToEndTime[args.curationHash],
            "Curation end time not reached"
        );

        curationHashToEnded[args.curationHash] = true;

        // No vote for the curation was casted on this chain
        if (curationHashToEndTime[args.curationHash] == 0) {
            return;
        }

        Vote[] storage _votes = votes[args.curationHash];

        uint256[] memory values = new uint256[](5); // numCardsRedeemed, numVotersRewarded, redeemIdx, rewardIdx, burnAmount

        for (uint256 i = 0; i < _votes.length; i++) {
            Vote memory _vote = _votes[i];

            if (
                args.rejected ||
                !_vote.bet ||
                _vote.loanAmount == args.winningOption
            ) {
                values[0]++;
            }

            if (
                !args.rejected &&
                _vote.bet &&
                _vote.loanAmount == args.winningOption
            ) {
                values[1]++;
            }
        }

        address[] memory votersCardRedeemable = new address[](values[0]);
        address[] memory votersRewarded = new address[](values[1]);
        uint256[] memory rewards = new uint256[](values[1]);

        for (uint256 i = 0; i < _votes.length; i++) {
            Vote storage _vote = _votes[i];

            if (
                args.rejected ||
                !_vote.bet ||
                _vote.loanAmount == args.winningOption
            ) {
                _vote.vpcRedeemable = true;
                votersCardRedeemable[values[2]++] = _vote.voter;
            }

            if (!args.rejected && _vote.bet) {
                // Reward winning votes with NEND
                if (_vote.loanAmount == args.winningOption) {
                    _vote.reward = rewards[values[3]] =
                        (_vote.cardLevel * args.totalRewards) /
                        args.winningPower;
                    votersRewarded[values[3]++] = _vote.voter;
                }
                // Burn NEND from losing votes
                else {
                    values[4] += PERIv2(vpcAddresses[_vote.cardLevel - 1])
                        .MINT_PRICE();
                }
                // End result on all chains: Total Burned == Total rewarded => Change in supply = 0
            }
        }

        if (values[4] > 0) {
            uint256 poolBalance = IERC20Upgradeable(nend).balanceOf(
                address(rewardPool)
            );
            rewardPool.burn(
                address(nend),
                values[4] > poolBalance ? poolBalance : values[4]
            );
        }

        emit CurationEnded(
            args.curationHash,
            votersCardRedeemable,
            votersRewarded,
            rewards
        );
    }

    function claimReward(
        bytes32 _curationHash,
        bytes memory _signature
    ) external virtual {
        bytes32 messageHash = keccak256(
            abi.encodePacked(msg.sender, _curationHash)
        );

        require(
            messageHash.recoverSigner(_signature) == owner(),
            "Invalid signature"
        );

        Vote[] storage _votes = votes[_curationHash];

        for (uint256 i = 0; i < _votes.length; i++) {
            if (_votes[i].voter != msg.sender) {
                continue;
            }

            Vote storage _vote = _votes[i];

            require(!_vote.rewardsClaimed, "Already claimed");
            require(_vote.reward > 0, "No reward");
            _vote.rewardsClaimed = true;

            nend.mint(msg.sender, _vote.reward);
            emit RewardClaimed(_curationHash, msg.sender);
            return;
        }

        revert("Not voted");
    }

    function getVoteCount(
        bytes32 _curationHash
    ) external view virtual override returns (uint256) {
        return votes[_curationHash].length;
    }

    function _authorizeUpgrade(address) internal override onlyOwner {}
}
