contracts/voting/PeriFiCuration.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../helpers/SignatureHelper.sol";
import "../vault/Vault.sol";
import "./ScenarioOneV2.sol";
import "../access/MWOwnable.sol";
import "../token/ERC20/NEND.sol";
import "./ScenarioOneV2.sol";
import "../vault/BalanceNames.sol";
import "./interfaces/ICuration.sol";

contract PeriFiCuration is ICuration, MWOwnable, SignatureHelper, BalanceNames {
    // Curation hash => votes
    mapping(bytes32 => Vote[]) public votes;
    mapping(bytes32 => uint48) public curationHashToEndTime;
    mapping(bytes32 => bool) public curationHashToEnded;
    address[4] public vpcAddresses;
    uint256 ongoingCurationCount;
    Vault public rewardPool;
    
    NEND public nend;

    constructor(
        Vault _rewardPool,
        NEND _rewardToken,
        address[4] memory _vpcAddresses
    ) {
        require(_vpcAddresses.length == 4, "Invalid number of vpc addresses");
        rewardPool = _rewardPool;
        nend = _rewardToken;
        vpcAddresses = _vpcAddresses;
    }

    function setRewardPool(Vault _rewardPool) external onlyOwner {
        rewardPool = _rewardPool;
    }

    function setNend(NEND _nend) external onlyOwner {
        nend = _nend;
    }

    function setVPCAddress(uint8 _level, address _address) external onlyOwner {
        vpcAddresses[_level - 1] = _address;
    }

    function setVPCAddresses(address[4] memory _addresses) external onlyOwner {
        vpcAddresses = _addresses;
    }

    function castVote(
        bool _accept,
        bool _bet,
        bool _leverageLending,
        uint8 _loanAmount,
        uint8 _duration,
        uint8 _apr,
        uint8 _cardLevel,
        uint256 _cardId,
        uint48 _curationEnd,
        bytes32 _curationHash,
        bytes memory _signature
    ) external override {
        bytes32 messageHash = keccak256(
            abi.encodePacked(_curationEnd, msg.sender, _curationHash)
        );

        require(
            recoverSigner(messageHash, _signature) == owner(),
            "Invalid signature"
        );

        require(_cardLevel >= 1 && _cardLevel < 5, "Invalid card level");

        require(
            PERIv2(vpcAddresses[_cardLevel - 1]).revealChecked(_cardId),
            "Card not releaved"
        );

        if (curationHashToEndTime[_curationHash] != 0) {
            require(
                curationHashToEndTime[_curationHash] == _curationEnd,
                "Curation end mismatch"
            );
        } else {
            curationHashToEndTime[_curationHash] = _curationEnd;
            ongoingCurationCount++;
        }

        for (uint256 i = 0; i < votes[_curationHash].length; i++) {
            if (votes[_curationHash][i].voter == msg.sender) {
                revert("Already voted");
            }
        }

        require(
            vpcAddresses[_cardLevel - 1] != address(0),
            "VPC address not set"
        );

        IERC721(vpcAddresses[_cardLevel - 1]).transferFrom(
            msg.sender,
            address(this),
            _cardId
        );

        uint48 voteTime = uint48(block.timestamp);

        votes[_curationHash].push(
            Vote(
                _accept,
                _bet,
                _leverageLending,
                false,
                false,
                _loanAmount,
                _duration,
                _apr,
                _cardLevel,
                _cardId,
                voteTime,
                msg.sender
            )
        );

        emit VoteCasted(
            _accept,
            _bet,
            _leverageLending,
            _loanAmount,
            _duration,
            _apr,
            _cardLevel,
            _cardId,
            voteTime,
            _curationHash,
            msg.sender
        );
    }

    function redeemVpc(bytes32 _curationHash) external override {
        Vote[] storage _votes = votes[_curationHash];

        for (uint i = 0; i < _votes.length; i++) {
            Vote storage _vote = _votes[i];
            if (_vote.voter == msg.sender && _vote.vpcRedeemable) {
                _vote.vpcRedeemable = false;
                IERC721(vpcAddresses[_vote.cardLevel - 1]).transferFrom(
                    address(this),
                    _vote.voter,
                    _vote.cardId
                );

                emit VPCRedeemed(_curationHash, msg.sender);
                return;
            }
        }

        revert("No vote valid for vpc redeem");
    }

    function endCuration(EndCurationArgs memory args) external override  onlyOwner {
        require(!curationHashToEnded[args.curationHash], "Curation ended");

        require(
            curationHashToEndTime[args.curationHash] == 0 ||
                block.timestamp >= curationHashToEndTime[args.curationHash],
            "Curation end time not reached"
        );

        curationHashToEnded[args.curationHash] = true;

        // No vote for the curation was casted on this chain
        if (curationHashToEndTime[args.curationHash] == 0) {
            return;
        }

        Vote[] storage _votes = votes[args.curationHash];

        uint256[] memory values = new uint256[](6); // numCardsRedeemed, numVotersRewarded, redeemIdx, rewardIdx, burnAmount, iflRewardPerVote

        for (uint256 i = 0; i < _votes.length; i++) {
            Vote memory _vote = _votes[i];

            if (
                args.rejected ||
                !_vote.bet ||
                _vote.loanAmount == args.winningOption
            ) {
                values[0]++;
            }

            if (
                !args.rejected &&
                _vote.bet &&
                _vote.loanAmount == args.winningOption
            ) {
                values[1]++;
            }
        }

        if (ongoingCurationCount > 0 && values[1] > 0) {
            values[5] =
                rewardPool.getNamedBalance(
                    interestForLenderBalanceName,
                    address(nend)
                ) /
                ongoingCurationCount /
                values[1];
        }

        address[] memory votersCardRedeemable = new address[](values[0]);
        address[] memory votersRewarded = new address[](values[1]);
        uint256[] memory rewards = new uint256[](values[1]);

        for (uint256 i = 0; i < _votes.length; i++) {
            Vote storage _vote = _votes[i];

            if (
                args.rejected ||
                !_vote.bet ||
                _vote.loanAmount == args.winningOption
            ) {
                _vote.vpcRedeemable = true;
                votersCardRedeemable[values[2]++] = _vote.voter;
            }

            if (!args.rejected && _vote.bet) {
                // Reward winning votes with NEND
                if (_vote.loanAmount == args.winningOption) {
                    uint256 vpcSaleReward = (_vote.cardLevel *
                        args.totalRewards) / args.winningPower;
                    rewards[values[3]] = vpcSaleReward + values[5];
                    votersRewarded[values[3]++] = _vote.voter;
                    _vote.rewardsClaimed = true;

                    nend.mint(_vote.voter, vpcSaleReward);
                    rewardPool.transferERC20(
                        address(nend),
                        _vote.voter,
                        values[5]
                    );
                    rewardPool.namedBalanceSpend(
                        interestForLenderBalanceName,
                        address(nend),
                        values[5]
                    );
                }
                // Burn NEND from losing votes
                else {
                    values[4] += PERIv2(vpcAddresses[_vote.cardLevel - 1])
                        .MINT_PRICE();
                }

                // End result on all chains: Total Burned == Total rewarded => Change in supply = 0
            }
        }

        if (values[4] > 0) {
            rewardPool.burn(address(nend), values[4]);
        }

        ongoingCurationCount--;

        emit CurationEnded(
            args.curationHash,
            votersCardRedeemable,
            votersRewarded,
            rewards
        );
    }
}

contracts/helpers/SignatureHelper.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

contract SignatureHelper {
    function recoverSigner(bytes32 messageHash, bytes memory signature)
        internal
        pure
        returns (address)
    {
        (bytes32 r, bytes32 s, uint8 v) = splitSig(signature);

        bytes memory prefix = "\x19Ethereum Signed Message:\n32";
        bytes32 prefixedHashMessage = keccak256(
            abi.encodePacked(prefix, messageHash)
        );

        return ecrecover(prefixedHashMessage, v, r, s);
    }

    function splitSig(bytes memory signature)
        internal
        pure
        returns (
            bytes32 r,
            bytes32 s,
            uint8 v
        )
    {
        assembly {
            r := mload(add(signature, 32))
            s := mload(add(signature, 64))
            v := and(mload(add(signature, 65)), 255)
        }
        if (v < 27) v += 27;
    }
}

