// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;
pragma abicoder v2;

import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router01.sol";
import "@uniswap/lib/contracts/libraries/TransferHelper.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../access/MWOwnable.sol";
import "./Vault.sol";

contract VaultProxy is MWOwnable {
    IUniswapV2Router01 public swapRouter;
    Vault public targetVault;
    string public balanceName;
    address[] public tokensIn;
    mapping(address => address) public tokenInToOut;

    uint24 public constant poolFee = 3000;

    constructor(string memory _balanceName) {
        balanceName = _balanceName;
    }

    function initialize(
        address[] memory _tokensIn,
        address[] memory _tokensOut,
        Vault _targetVault,
        address _swapRouter
    ) external onlyOwner {
        targetVault = _targetVault;
        swapRouter = IUniswapV2Router01(_swapRouter);
        require(
            _tokensIn.length == _tokensOut.length,
            "Tokens in and tokens out not matching in length"
        );

        for (uint256 i = 0; i < _tokensIn.length; i++) {
            address _tokenIn = _tokensIn[i];
            address _tokenOut = _tokensOut[i];
            _tokenIn = _tokenIn == address(0) ? swapRouter.WETH() : _tokenIn;
            _tokenOut = _tokenIn == address(0) ? swapRouter.WETH() : _tokenOut;
            require(tokenInToOut[_tokenIn] == address(0), "Duplicate token in");
            tokenInToOut[_tokenIn] = _tokenOut;
            tokensIn.push(_tokenIn);
        }
    }

    function setTargetVault(Vault _targetVault) external onlyOwner {
        targetVault = _targetVault;
    }

    function addToken(address _tokenIn, address _tokenOut) external onlyOwner {
        _tokenIn = _tokenIn == address(0) ? swapRouter.WETH() : _tokenIn;
        tokensIn.push(_tokenIn);
        tokenInToOut[_tokenIn] = _tokenOut;
    }

    function removeToken(address _tokenIn) external onlyOwner {
        _tokenIn = _tokenIn == address(0) ? swapRouter.WETH() : _tokenIn;
        tokenInToOut[_tokenIn] = address(0);
    }

    function hasPendingTransfer()
        external
        view
        returns (bool _hasPendingTransfer)
    {
        for (uint256 i = 0; i < tokensIn.length; i++) {
            address _tokenIn = tokensIn[i];
            bool _nativeIn = _tokenIn == swapRouter.WETH();

            // Token removed
            if (tokenInToOut[_tokenIn] == address(0)) {
                continue;
            }

            if (
                (!_nativeIn && IERC20(_tokenIn).balanceOf(address(this)) > 0) ||
                (_nativeIn && address(this).balance > 0)
            ) {
                _hasPendingTransfer = true;
                break;
            }
        }
    }

    function transfer() external onlyOwner {
        for (uint256 i = 0; i < tokensIn.length; i++) {
            address _tokenIn = tokensIn[i];
            address _tokenOut = tokenInToOut[_tokenIn];

            // Token removed
            if (_tokenOut == address(0)) {
                continue;
            }

            bool _nativeIn = _tokenIn == swapRouter.WETH();
            bool _nativeOut = _tokenOut == swapRouter.WETH();

            uint256 _amountIn = _nativeIn
                ? address(this).balance
                : IERC20(_tokenIn).balanceOf(address(this));

            if (_amountIn == 0) {
                continue;
            }

            uint256 _amountOut = _amountIn;

            // Conversion needed
            if (_tokenIn != _tokenOut) {
                address[] memory _path = new address[](2);
                _path[0] = _tokenIn;
                _path[1] = _tokenOut;
                uint256[] memory _amounts;

                if (!_nativeIn) {
                    TransferHelper.safeApprove(
                        _tokenIn,
                        address(swapRouter),
                        _amountIn
                    );
                }

                // Case 1 ERC20 -> Native
                if (!_nativeIn && _nativeOut) {
                    _amounts = swapRouter.swapExactTokensForETH(
                        _amountIn,
                        0,
                        _path,
                        address(targetVault),
                        block.timestamp
                    );
                }
                // Case 2 Native -> ERC20
                else if (_nativeIn && !_nativeOut) {
                    _amounts = swapRouter.swapExactETHForTokens{
                        value: _amountIn
                    }(0, _path, address(targetVault), block.timestamp);
                }
                // Case 3 ERC20 -> ERC20
                else {
                    _amounts = swapRouter.swapExactTokensForTokens(
                        _amountIn,
                        0,
                        _path,
                        address(targetVault),
                        block.timestamp
                    );
                }

                _amountOut = _amounts[1];
            }
            // in == out and is ERC20
            else if (!_nativeIn) {
                TransferHelper.safeTransfer(
                    _tokenOut,
                    address(targetVault),
                    _amountIn
                );
            }
            // in == out and is Native
            else {
                TransferHelper.safeTransferETH(address(targetVault), _amountIn);
            }

            targetVault.namedBalanceReceive(
                balanceName,
                _nativeOut ? address(0) : _tokenOut,
                _amountOut
            );
        }
    }

    function terminate(address receiver) external onlyOwner {
        for (uint256 i = 0; i < tokensIn.length; i++) {
            address _tokenIn = tokensIn[i];
            bool _nativeIn = _tokenIn == swapRouter.WETH();

            if (!_nativeIn) {
                TransferHelper.safeTransfer(
                    _tokenIn,
                    receiver,
                    IERC20(_tokenIn).balanceOf(address(this))
                );
            }
        }
        selfdestruct(payable(receiver));
    }

    receive() external payable {}

    fallback() external payable {}
}

