        );

        if (revealChecked[tokenId] == false) {
            return notRevealedURI;
        }
        string memory _tokenURI = _tokenURIs[tokenId];
        return _tokenURI;
    }

    /* ========== INTERNAL FUNCTIONS ========== */

    /**
     * @dev Hook that is called before any token transfer. This includes minting
     * and burning.
     *
     * Calling conditions:
     *
