pragma solidity >=0.8.7;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import { AmountDeriver } from "../lib/AmountDeriver.sol";
import "../PeriFiAdmin.sol";
import "./LoanAuctionEnums.sol";
import "./LoanAuctionEventsAndErrors.sol";
import "./LoanAuctionStructs.sol";
import "./LoanRepaymentCalculator.sol";
import "../vault/Vault.sol";
import "../access/MWOwnable.sol";
import "hardhat/console.sol";

contract LoanAuction is MWOwnable, ReentrancyGuard, AmountDeriver, LoanAuctionEventsAndErrors {
    uint256 public auctionCount = 0;
    mapping(uint256 => Auction) public auctions;
    mapping(uint256 => AuctionStatus) public status;
    mapping(uint256 => uint256) public bidAmounts;
    mapping(uint256 => address) public bidders;
    address public periFiAdminAddr;
    address public loanRepaymentAddr;

    constructor(address _periFiAdminAddr, address _loanRepaymentAddr) {
        periFiAdminAddr = _periFiAdminAddr;
        loanRepaymentAddr = _loanRepaymentAddr;
    }

    // display current price

    // display price for borrower

    function beginAuction(
        AuctionParameters calldata params
    ) external onlyOwner nonReentrant {
        Auction memory auction = Auction({
            id: auctionCount,
            protectedUntil: 0,
            parameters: params
        });
        
        address owner = IERC721(params.tokenAddress).ownerOf(params.tokenId);

        // We will assume auction contract will hold the defaulted collateral for ease of use.
        if (owner != address(this)) {
            revert CollateralOwnershipNotTransferred();
        }

        if (params.startAmount <= params.endAmount) {
            revert InvalidPriceRange(params.startAmount, params.endAmount);
        }

        if (params.startTime >= params.endTime) {
            revert InvalidTimeRange(params.startTime, params.endTime);
        }

        if (block.timestamp >= params.endTime) {
            revert InvalidTimeRange(params.startTime, params.endTime);
        }

        if (params.stepDownConfig.length != 0) {
            validateStepDownConfig(params.startTime, params.endTime, params.stepDownConfig);
        }

        // set liquidation protection expiration
        if (params.isProtected){
            uint256 protection = PeriFiAdmin(periFiAdminAddr).liquidateProtectionDuration();
            auction.protectedUntil = params.startTime + protection;
        }

        auctions[auctionCount] = auction;
        status[auctionCount] = AuctionStatus.Registered;
        auctionCount += 1;
        emit LoanAuctionBegun(auction.id, params.loanId, params.tokenAddress, params.paymentToken,
            params.loanBorrower, params.tokenId, params.startAmount, params.endAmount,
            params.startTime, params.endTime, auction.protectedUntil, params.loanRepaymentAmount,
            params.stepDownConfig);
    }

    function makeBid(uint256 auctionId) external nonReentrant returns (bool completed) {
        if (status[auctionId] != AuctionStatus.Registered) {
            revert AuctionInvalidStatus(auctionId, status[auctionId]);
        }

        uint256 currentPrice = currentBidPrice(auctionId);

        Auction memory item = auctions[auctionId];
        AuctionParameters memory params = item.parameters;

        if (item.protectedUntil > block.timestamp) {
            // hold the asset until the protection period is over
            IERC20(params.paymentToken).transferFrom(msg.sender, address(this), currentPrice);
            bidders[auctionId] = msg.sender;
            status[auctionId] = AuctionStatus.Protected;
            bidAmounts[auctionId] = currentPrice;
            emit LoanAuctionSoldAndProtected(auctionId, msg.sender, params.loanBorrower, currentPrice);
            return false;
        }

        IERC721(params.tokenAddress).transferFrom(address(this), msg.sender, params.tokenId);
        
        // transfer erc20 from this contract to all the recipients
        LoanRepaymentCalculator calculator = LoanRepaymentCalculator(loanRepaymentAddr);
        Repayment[] memory repayments = calculator.getDestinations(
            params.loanId, 
            params.loanRepaymentAmount,
            params.loanAmount,
            params.lender);
        address insuranceVaultAddr = calculator.insuranceVaultAddress();
        IERC20 erc20 = IERC20(params.paymentToken);
        uint256 bidAmountLeft = currentPrice;
        uint256 insuranceLeft = erc20.balanceOf(insuranceVaultAddr);
        uint repaymentLength = repayments.length;
        for (uint idx = 0; idx < repaymentLength; idx++) {
            Repayment memory repaymentItem = repayments[idx];
            uint256 paymentLeft = repaymentItem.value;

            // cover the repayment from deposit amount from bid
            if (bidAmountLeft > 0) {
                if (bidAmountLeft >= paymentLeft) {
                    console.log('sending from %s to %s, %s', msg.sender, repaymentItem.targetAddress, paymentLeft);
                    erc20.transferFrom(msg.sender, repaymentItem.targetAddress, paymentLeft);
                    bidAmountLeft -= paymentLeft;
                    continue;
                }
                console.log('sending partial from %s to %s, %s', msg.sender,repaymentItem.targetAddress, bidAmountLeft);
                erc20.transferFrom(msg.sender, repaymentItem.targetAddress, bidAmountLeft);
                paymentLeft -= bidAmountLeft;
                bidAmountLeft = 0;
            }
            if (insuranceVaultAddr == repaymentItem.targetAddress) {
                    continue;
            }
            // if the repayment wasn't covered fully, send it from insurance
            if (insuranceLeft >= paymentLeft) {
                console.log('sending from IV to %s, %s',repaymentItem.targetAddress, paymentLeft);
                
                Vault(payable(insuranceVaultAddr)).transferERC20(params.paymentToken, repaymentItem.targetAddress, paymentLeft);
                insuranceLeft -= paymentLeft;
                continue;
            }

            if (insuranceLeft > 0){
                console.log('sending partial from IV to %s, %s',repaymentItem.targetAddress, insuranceLeft);
                Vault(payable(insuranceVaultAddr)).transferERC20(params.paymentToken, repaymentItem.targetAddress, insuranceLeft);
            }
            break;
        }
        if (bidAmountLeft > 0) {
            console.log('sending from %s to %s, %s', msg.sender, params.loanBorrower, bidAmountLeft);
            erc20.transferFrom(msg.sender, params.loanBorrower, bidAmountLeft);
        }
        emit LoanAuctionSoldToBidder(auctionId, msg.sender, currentPrice);
        status[auctionId] = AuctionStatus.SoldToBidder;
        return true;
    }

    function claim(uint256 auctionId) external nonReentrant {
        if (status[auctionId] != AuctionStatus.Protected) {
            revert AuctionInvalidStatus(auctionId, status[auctionId]);
        }

        Auction memory item = auctions[auctionId];

        AuctionParameters memory params = item.parameters;
        
        if (block.timestamp < item.protectedUntil) {
            revert ProtectionNotOver();
        }

        IERC721(params.tokenAddress).transferFrom(address(this), msg.sender, params.tokenId);
        status[auctionId] = AuctionStatus.SoldToBidder;
        uint256 bidAmount = bidAmounts[auctionId];
        address bidder = bidders[auctionId];

        // transfer erc20 from this contract to all the recipients
        LoanRepaymentCalculator calculator = LoanRepaymentCalculator(loanRepaymentAddr);
        Repayment[] memory repayments = calculator.getDestinations(
            params.loanId, 
            params.loanRepaymentAmount,
            params.loanAmount,
            params.lender);

        address insuranceVaultAddr = calculator.insuranceVaultAddress();
        IERC20 erc20 = IERC20(params.paymentToken);
        uint256 bidAmountLeft = bidAmount;
        uint256 insuranceLeft = erc20.balanceOf(insuranceVaultAddr);
        uint repaymentLength = repayments.length;
        for (uint idx = 0; idx < repaymentLength; idx++) {
            Repayment memory repaymentItem = repayments[idx];
            uint256 paymentLeft = repaymentItem.value;

            // cover the repayment from deposit amount from bid
            if (bidAmountLeft > 0) {
                if (bidAmountLeft >= paymentLeft) {
                    console.log('sending from %s to %s, %s', address(this), repaymentItem.targetAddress, paymentLeft);
                    erc20.transfer(repaymentItem.targetAddress, paymentLeft);
                    bidAmountLeft -= paymentLeft;
                    continue;
                }
                console.log('sending partial from %s to %s, %s', address(this),repaymentItem.targetAddress, bidAmountLeft);
                erc20.transfer(repaymentItem.targetAddress, bidAmountLeft);
                paymentLeft -= bidAmountLeft;
                bidAmountLeft = 0;
            }
            if (insuranceVaultAddr == repaymentItem.targetAddress) {
                    continue;
            }
            // if the repayment wasn't covered fully, send it from insurance
            if (insuranceLeft >= paymentLeft) {
                console.log('sending from IV to %s, %s',repaymentItem.targetAddress, paymentLeft);
                Vault(payable(insuranceVaultAddr)).transferERC20(params.paymentToken, repaymentItem.targetAddress, paymentLeft);
                insuranceLeft -= paymentLeft;
                continue;
            }

            if (insuranceLeft > 0){
                console.log('sending partial from IV to %s, %s',repaymentItem.targetAddress, insuranceLeft);
                Vault(payable(insuranceVaultAddr)).transferERC20(params.paymentToken, repaymentItem.targetAddress, insuranceLeft);
            }
            break;
        }
        if (bidAmountLeft > 0) {
            console.log('sending from %s to %s, %s', address(this), params.loanBorrower, bidAmountLeft);
            erc20.transfer(params.loanBorrower, bidAmountLeft);
        }
        emit LoanAuctionSoldToBidder(auctionId, bidder, bidAmount);
    }
    

    function payout(uint256 auctionId) external nonReentrant {
        AuctionStatus currentStatus = status[auctionId];
        
        if (currentStatus != AuctionStatus.Registered
            && currentStatus != AuctionStatus.Protected) {
            revert AuctionInvalidStatus(auctionId, currentStatus);
        }

        Auction memory item = auctions[auctionId];
        AuctionParameters memory params = item.parameters;

        if (msg.sender != params.loanBorrower) {
            revert NotAuthorized();
        }

        // check auction's protection period has been expired
        if (block.timestamp > item.protectedUntil) {
            revert ProtectionOver();
        }

        uint256 repayment = params.loanRepaymentAmount;
        uint256 penalty = 0;
        if (currentStatus == AuctionStatus.Protected) {
            uint bidAmount = bidAmounts[auctionId];
            penalty = bidAmount * 5 / 100;
            IERC20(params.paymentToken).transferFrom(msg.sender, bidders[auctionId], penalty);
            IERC20(params.paymentToken).transfer(bidders[auctionId], bidAmount);
            bidAmounts[auctionId] = 0;
        }
        uint256 fullRepayment = repayment + penalty;
        IERC721(params.tokenAddress).transferFrom(address(this), msg.sender, params.tokenId);
        
        LoanRepaymentCalculator calculator = LoanRepaymentCalculator(loanRepaymentAddr);
        Repayment[] memory repayments = calculator.getDestinations(
            params.loanId, 
            params.loanRepaymentAmount,
            params.loanAmount,
            params.lender);
        IERC20 erc20 = IERC20(params.paymentToken);
        uint repaymentLength = repayments.length;
        for (uint idx = 0; idx < repaymentLength; idx++) {
            Repayment memory repaymentItem = repayments[idx];
            erc20.transferFrom(msg.sender, repaymentItem.targetAddress, repaymentItem.value);
        }
        status[auctionId] = AuctionStatus.SoldToBorrower;
        emit LoanAuctionCancelledByPayout(auctionId, msg.sender, fullRepayment);
    }

    function currentDebt(uint256 auctionId) public view returns (uint256 debt) {
        AuctionStatus currentStatus = status[auctionId];
        
        if (currentStatus != AuctionStatus.Registered
            && currentStatus != AuctionStatus.Protected) {
            return 0;
        }

        Auction memory item = auctions[auctionId];
        AuctionParameters memory params = item.parameters;

        if (msg.sender != params.loanBorrower) {
            return 0;
        }

        uint256 repayment = params.loanRepaymentAmount;
        uint256 penalty = 0;
        if (currentStatus == AuctionStatus.Protected) {
            uint bidAmount = bidAmounts[auctionId];
            penalty = bidAmount * 5 / 100;
        }
        return repayment + penalty;
    }
    
    function currentBidPrice(uint256 auctionId) public view returns (uint256 amount) {
        if (status[auctionId] != AuctionStatus.Registered) {
            return 0;
        }

        Auction memory item = auctions[auctionId];
        AuctionParameters memory params = item.parameters;
        if (params.stepDownConfig.length == 0) {
            // no stepDown
            return _locateCurrentAmount(
                params.startAmount, 
                params.endAmount, 
                params.startTime, 
                params.endTime, 
                true
            );
        } 

        // stepDown
        return _locateCurrentStepDownAmount(
            params.startAmount,
            params.endAmount,
            params.startTime,
            params.stepDownConfig,
            true
        );
    }

    function validateStepDownConfig(
        uint256 startTime,
        uint256 endTime,
        bytes memory config
    ) internal pure {
        if (config.length <= 0) {
            revert InvalidStepDownConfig();
        }

        uint accDecrement;
        uint8 actualLength;
        uint length = config.length;
        for (uint idx = 0; idx < length; idx++){
            uint8 decrement = uint8(config[idx]);
            if (decrement == 255) {
                break;
            }
            accDecrement += decrement;
            actualLength++;
        }

        uint256 duration = endTime - startTime;
        uint256 minDuration = uint256(actualLength * 1 days);
        uint256 maxDuration = uint256((actualLength+2) * 1 days);
        if (duration <= minDuration) {
            revert StepDownConfigAndAuctionTimeMismatch();
        }
        if (duration >= maxDuration) {
            revert StepDownConfigAndAuctionTimeMismatch();
        }
    }

    function _locateCurrentStepDownAmount(
        uint256 startAmount,
        uint256 endAmount,
        uint256 startTime,
        bytes memory stepDownConfig,
        bool roundUp
    ) internal view returns (uint256 amount) {
        // Only modify end amount if it doesn't already equal start amount.
        if (startAmount != endAmount) {
            // Declare variables to derive in the subsequent unchecked scope.
            uint256 duration;
            uint256 elapsed;
            uint256 remaining;

            uint256 elapsedTs;
            uint accDecrement;
            uint totalDecrement;
            // Skip underflow checks as startTime <= block.timestamp < endTime.
            unchecked {
                // Derive the duration for the order and place it on the stack.
                // duration = endTime - startTime;

                // Derive time elapsed since the order started & place on stack.
                elapsedTs = block.timestamp - startTime;

                // Derive time remaining until order expires and place on stack.
                // remaining = duration - elapsed;
            }
            uint256 daysPassed = elapsedTs / 1 days;
            uint length = stepDownConfig.length;
            for (uint idx = 0; idx < length; idx++){
                uint8 decrement = uint8(stepDownConfig[idx]);
                if (decrement == 255) {
                    break;
                }
                if (idx < daysPassed) {
                    accDecrement += decrement;
                }
                totalDecrement += decrement;
            }
            duration = totalDecrement;
            elapsed = accDecrement;
            remaining = duration - elapsed;
            // Aggregate new amounts weighted by time with rounding factor.
            uint256 totalBeforeDivision = ((startAmount * remaining) +
                (endAmount * elapsed));

            // Use assembly to combine operations and skip divide-by-zero check.
            assembly {
                // Multiply by iszero(iszero(totalBeforeDivision)) to ensure
                // amount is set to zero if totalBeforeDivision is zero,
                // as intermediate overflow can occur if it is zero.
                amount := mul(
                    iszero(iszero(totalBeforeDivision)),
                    // Subtract 1 from the numerator and add 1 to the result if
                    // roundUp is true to get the proper rounding direction.
                    // Division is performed with no zero check as duration
                    // cannot be zero as long as startTime < endTime.
                    add(
                        div(sub(totalBeforeDivision, roundUp), duration),
                        roundUp
                    )
                )
            }

            // Return the current amount.
            return amount;
        }

        // Return the original amount as startAmount == endAmount.
        return endAmount;
    }
}","keccak256":"0x46aa8fb554302b0750082923835e3fd59556c5b82b1f88db59ce3f0aa53eb5d0","license":"MIT"},"contracts/loan/LoanAuctionEnums.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.7;

enum AuctionStatus {
    NotRegistered,
    Registered,
    Protected,
    SoldToBidder,
    SoldToBorrower
}

enum RecipientType {
    LendingPool,
    Lender,
    Commission,
    Borrower
}","keccak256":"0xba2ef7a8ce89271cf196f313419eab308a84850879002622c664e672d912f3a4","license":"MIT"},"contracts/loan/LoanAuctionEventsAndErrors.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.7;

import "./LoanAuctionEnums.sol";

interface LoanAuctionEventsAndErrors{
    error InvalidStepDownConfig();
    error StepDownConfigOverflow();
    error IncompleteStepDownConfig();
    error StepDownConfigAndAuctionTimeMismatch();
    
    error CollateralOwnershipNotTransferred();
    error AuctionInvalidStatus(uint256 auctionId, AuctionStatus status);
    error AuctionNotRegistered();
    error ProtectionNotOver();
    error ProtectionOver();
    error InvalidPriceRange(uint256 startAmount, uint256 endAmount);
    error InvalidTimeRange(uint256 startTime, uint256 endTime);
    error NotAuthorized();

    event LoanAuctionBegun(uint256 indexed auctionId, uint256 loanId, 
        address tokenAddr, address paymentToken, 
        address loanBorrower, uint256 tokenId, uint256 startAmount, uint256 endAmount,
        uint256 startTime, uint256 endTime, uint256 protectedUntil, uint256 loanRepaymentAmount,
        bytes stepDownConfig);
    event LoanAuctionSoldAndProtected(uint256 indexed auctionId, address bidder, address borrower, uint256 bidAmount);
    event LoanAuctionSoldToBidder(uint256 indexed auctionId, address bidder, uint256 bidAmount);
    event LoanAuctionCancelledByPayout(uint256 indexed auctionId, address borrower, uint256 payout);
}","keccak256":"0x8c5757fb16cd612b72114303bd0ae5401d524d5f77d01a77718f126ac0c3815d","license":"MIT"},"contracts/loan/LoanAuctionStructs.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.7;

import "./LoanAuctionEnums.sol";

struct AuctionParameters {
    uint256 loanId;
    address tokenAddress;
    address paymentToken;
    uint256 tokenId;
    uint256 startAmount;
    uint256 endAmount;
    uint256 startTime;
    uint256 endTime;
    bytes stepDownConfig;
    address loanBorrower;
    uint256 loanAmount;
    uint256 loanRepaymentAmount;
    address lender;
    bool isProtected;
}


struct PaymentRecipients {
    address recipient;
    uint256 amount;
    RecipientType recipientType;
}

struct Auction {
    uint256 id;
    uint256 protectedUntil;
    AuctionParameters parameters;
}","keccak256":"0x0593c2a7a4f3cd6ccfb9f508f9b64b88c1b7bcac30df95e70c31aeb20ea1cd31","license":"MIT"},"contracts/loan/LoanRepaymentCalculator.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.7;

import "../PeriFiAdmin.sol";
import "../vault/LendingPool.sol";

enum DestinationType {
    Pool,
    Lender,
    InsuranceVault,
    Commission,
    CurationRewardPool
}

struct Repayment {
    address targetAddress;
    uint256 value;
}


contract LoanRepaymentCalculator {
    address public insuranceVaultAddress;
    address public lendingPoolAddress;
    address public masterWalletAddress;
    address public periFiAdminAddress;
    address public curationRewardPoolAddress;

    constructor(
        address _insuranceVaultAddress,
        address _lendingPoolAddress,
        address _masterWalletAddress,
        address _periFiAdminAddress,
        address _curationRewardPoolAddress) {
        insuranceVaultAddress = _insuranceVaultAddress;
        lendingPoolAddress = _lendingPoolAddress;
        masterWalletAddress = _masterWalletAddress;
        periFiAdminAddress = _periFiAdminAddress;
        curationRewardPoolAddress = _curationRewardPoolAddress;
    }

    function getDestinations(
        uint256 loanId,
        uint256 repaymentAmount, 
        uint256 loanAmount,
        address lender
    ) external view returns (Repayment[] memory results) {
        uint256 interestDue = repaymentAmount - loanAmount;

        LendingPool lendingPool = LendingPool(payable(lendingPoolAddress));
        uint256 leverageAmount = lendingPool.loanToLeverage(loanId);

        // if there is no interest
        if (interestDue == 0) {
            if (leverageAmount == 0){
                results = new Repayment[](1);
                results[0] = Repayment(lender, loanAmount);
            } else {
                results = new Repayment[](2);
                results[0] = Repayment(lendingPoolAddress, leverageAmount);
                results[1] = Repayment(lender, loanAmount - leverageAmount);
            }
            return results;
        }

        uint256 interestForPool = interestDue * leverageAmount / loanAmount;
        uint256 interestForLender = interestDue - interestForPool;
        uint256 loanAmountByLender = loanAmount - leverageAmount;

        uint256 lenderForIFL;
        uint256 commissionForIFL;
        uint256 curationRewardForIFL;

        (
            lenderForIFL, 
            commissionForIFL, 
            curationRewardForIFL
        ) = distributeIFL(interestForLender);

        if (leverageAmount == 0) {
            // no leverage Used, only need to get InterestForLender (IFL)
            results = new Repayment[](3);
            results[0] = Repayment(lender, loanAmountByLender + lenderForIFL);
            results[1] = Repayment(masterWalletAddress, commissionForIFL);
            results[2] = Repayment(curationRewardPoolAddress, curationRewardForIFL);
        } else {
            PeriFiAdmin periFiAdmin = PeriFiAdmin(periFiAdminAddress);

            uint256 poolUsageInBasisPoints = lendingPool.loanToPoolUsageInBasisPoint(loanId);
            uint256 interestForIVInBasisPoints = periFiAdmin.interestForIVInBasisPoints();

            uint256 interestBasisPointForLender = lenderInterestInBasisPointByPoolUsage(poolUsageInBasisPoints);
            
            uint256 lenderForIFP = interestForPool * interestBasisPointForLender / 10000;
            uint256 insuranceVaultForIFP = interestForPool * interestForIVInBasisPoints / 10000;
            uint256 poolForIFP = interestForPool - (lenderForIFP + insuranceVaultForIFP);

            results = new Repayment[](5);
            results[0] = Repayment(lendingPoolAddress, leverageAmount + poolForIFP);
            results[1] = Repayment(lender, loanAmountByLender + lenderForIFP + lenderForIFL);
            results[2] = Repayment(insuranceVaultAddress, insuranceVaultForIFP);
            results[3] = Repayment(masterWalletAddress, commissionForIFL);
            results[4] = Repayment(curationRewardPoolAddress, curationRewardForIFL);

        }
    }

    function distributeIFL(
        uint256 interestForLender) internal view returns (
            uint256 lenderForIFL,
            uint256 commissionForIFL,
            uint256 curationRewardForIFL
        ) {
            PeriFiAdmin periFiAdmin = PeriFiAdmin(periFiAdminAddress);
            uint256 commissionInBasisPoints = periFiAdmin.loanCommissionInBasisPoints();
            if (commissionInBasisPoints < 100) {
                commissionInBasisPoints = 100; // to ensure curation reward commission
            }
            // no leverage Used, only need to get InterestForLender (IFL)
            uint256 totalCommission = interestForLender * commissionInBasisPoints / 10000;
            curationRewardForIFL = interestForLender / 100; // take 1%
            commissionForIFL = totalCommission - curationRewardForIFL;
            lenderForIFL = interestForLender - totalCommission;
        }

    function lenderInterestInBasisPointByPoolUsage(uint256 poolUsageInBasisPoints) internal pure returns (uint256 basisPoint) {
            if (poolUsageInBasisPoints <= 2000) {
                basisPoint = 5500;
            }
            else if (poolUsageInBasisPoints <= 3000) {
                basisPoint = 5000;
            }
            else if (poolUsageInBasisPoints <= 5000) {
                basisPoint = 4500;
            }
            else if (poolUsageInBasisPoints <= 6000) {
                basisPoint = 4000;
            }
            else if (poolUsageInBasisPoints <= 7000) {
                basisPoint = 3500;
            }
            else if (poolUsageInBasisPoints <= 8000) {
                basisPoint = 2500;
            }
            else if (poolUsageInBasisPoints <= 9000) {
                basisPoint = 1500;
            }
            else {
                basisPoint = 500;
            }
            return basisPoint;
    }

}","keccak256":"0x856f8527002b0722b8d40f7186d6d30bae4ed36eb2b027c57080409bb21ffda3","license":"MIT"},"contracts/vault/LendingPool.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.7;

import "./Vault.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

error NotSufficientBalanceInPool(uint256 loanId, uint256 balance, uint256 requested);
error IllegalLeverageAccess(address sender, uint256 loanId, uint256 requested);
error AlreadyLeveragedLoan(uint256 loanId);
contract LendingPool is Vault, ReentrancyGuard {

    constructor(string memory name) Vault(name) {
    }

    address public loanAddress;

    mapping (uint256 => bool) public leveragedLoan;

    mapping (uint256 => uint256) public loanToLeverage;

    mapping (uint256 => uint256) public loanToPoolUsageInBasisPoint;

    function leverageLoan(
        uint256 loanId, 
        address paymentToken, 
        uint256 leveragedAmount, 
        address borrower) external nonReentrant onlyRole("spender") {
        if (leveragedLoan[loanId]) {
            revert AlreadyLeveragedLoan(loanId);
        }

        if (leveragedAmount == 0) {
            leveragedLoan[loanId] = true;
            return;
        }

        IERC20 erc20 = IERC20(paymentToken);
        uint256 balance = erc20.balanceOf(address(this));

        if (balance < leveragedAmount) {
            revert NotSufficientBalanceInPool(loanId, balance, leveragedAmount);
        }

        erc20.transfer(borrower, leveragedAmount);
        uint256 poolUsageInBasisPoint = (leveragedAmount * 10000) / balance;
        loanToLeverage[loanId] = leveragedAmount;
        loanToPoolUsageInBasisPoint[loanId] = poolUsageInBasisPoint;
        leveragedLoan[loanId] = true;
    }
}","keccak256":"0x99f75900c0909faf88c855b9de75eba27fd47117ae29a144c9db3a3c0c5e7411","license":"MIT"},"contracts/vault/Vault.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "../access/SimpleRoleAccess.sol";

contract Vault is SimpleRoleAccess {
    bytes4 private ERC1155_INTERFACE_ID = 0xd9b67a26;
    string public name;

    mapping(address => bool) public authorizedOperators;
    mapping(address => bool) public authorizedSpenders;
    // Balance name => token => amount
    mapping(string => mapping(address => uint256)) public namedBalances;

    constructor(string memory _name) {
        name = _name;
    }

    function approveERC20Transfer(
        address _tokenAddress,
        address _spender,
        uint256 _amount
    ) external onlyRole("spender") returns (bool) {
        IERC20 erc20 = IERC20(_tokenAddress);
        return erc20.approve(_spender, _amount);
    }

    function transferERC20(
        address _tokenAddress,
        address _to,
        uint256 _amount
    ) external onlyRole("spender") returns (bool) {
        IERC20 erc20 = IERC20(_tokenAddress);
        return erc20.transfer(_to, _amount);
    }

    function setERC721ApprovalForAll(
        address _tokenAddress,
        address _operator,
        bool _approved
    ) external onlyRole("spender") {
        IERC721 erc721 = IERC721(_tokenAddress);
        erc721.setApprovalForAll(_operator, _approved);
    }

    function transferERC721(
        address _tokenAddress,
        address _to,
        uint256 _tokenId
    ) external onlyRole("spender") {
        IERC721 erc721 = IERC721(_tokenAddress);
        erc721.transferFrom(address(this), _to, _tokenId);
    }

    function transferERC1155(
        address _tokenAddress,
        address _to,
        uint256 _id,
        uint256 _value,
        bytes calldata _data
    ) external onlyRole("spender") {
        IERC1155 erc1155 = IERC1155(_tokenAddress);
        require(
            erc1155.supportsInterface(ERC1155_INTERFACE_ID),
            "given token address doesn't support ERC1155"
        );
        erc1155.safeTransferFrom(address(this), _to, _id, _value, _data);
    }

    function transferERC1155Batch(
        address _tokenAddress,
        address _to,
        uint256[] calldata _ids,
        uint256[] calldata _values,
        bytes calldata _data
    ) external onlyRole("spender") {
        IERC1155 erc1155 = IERC1155(_tokenAddress);
        require(
            erc1155.supportsInterface(ERC1155_INTERFACE_ID),
            "given token address doesn't support ERC1155"
        );
        erc1155.safeBatchTransferFrom(address(this), _to, _ids, _values, _data);
    }

    function setERC1155ApprovalForAll(
        address _tokenAddress,
        address _operator,
        bool _approved
    ) external onlyRole("spender") {
        IERC1155 erc1155 = IERC1155(_tokenAddress);
        require(
            erc1155.supportsInterface(ERC1155_INTERFACE_ID),
            "given token address doesn't support ERC1155"
        );
        erc1155.setApprovalForAll(_operator, _approved);
    }

    function getNativeBalance() external view returns (uint256) {
        return address(this).balance;
    }

    function transferNative(address payable _to, uint256 _amount)
        public
        payable
        onlyRole("spender")
    {
        (bool sent, ) = _to.call{ value: _amount }("");
        require(sent, "Failed to send Ether");
    }

    function burn(address _token, uint256 _amount)
        public
        onlyRole("spender")
    {
        ERC20Burnable(_token).burn(_amount);
    }

    function namedBalanceReceive(
        string memory _name,
        address _token,
        uint256 _amount
    ) external onlyRole("spender") {
        namedBalances[_name][_token] += _amount;
    }

    function namedBalanceSpend(
        string memory _name,
        address _token,
        uint256 _amount
    ) external onlyRole("spender") {
        require(
            namedBalances[_name][_token] >= _amount,
            "Insufficient balance"
        );
        namedBalances[_name][_token] -= _amount;
    }

    function getNamedBalance(string memory _name, address _token)
        external
        view
        returns (uint256)
    {
        uint256 balance = namedBalances[_name][_token];
        uint256 actualBalance = IERC20(_token).balanceOf(address(this));

        return balance <= actualBalance ? balance : actualBalance;
    }

    receive() external payable {}

    fallback() external payable {}
}
