pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "../staking/interfaces/ILendingPoolStaking.sol";
import "../vault/Vault.sol";
import "../test/Testing.sol";
import "../access/MWOwnable.sol";

abstract contract Inflation is ERC20, MWOwnable, Testing {
    ILendingPoolStaking public staking;

    // The number of inflations carried out
    uint8 public inflationCount;

    // Decay rate * 10000
    uint16 public inflationDecay = 125; // 1.25%

    // The time of last inflation
    uint48 public lastInflation;

    uint48 public immutable inflationInterval = 1 weeks;

    uint48 public immutable testInflationInterval = 10 minutes;

    // The inflation amount
    uint256 public inflationAmount;

    constructor(uint256 _inflationAmount) {
        inflationAmount = _inflationAmount;
    }

    function setStaking(address _staking) external onlyOwner {
        staking = ILendingPoolStaking(_staking);
    }

    function setInflationCount(uint8 _inflationCount) external onlyOwner {
        inflationCount = _inflationCount;
    }

    function setInflationDecay(uint16 _inflationDecay) external onlyOwner {
        inflationDecay = _inflationDecay;
    }

    function setLastInflation(uint48 _lastInflation) external onlyOwner {
        lastInflation = _lastInflation;
    }

    function setInflationAmount(uint256 _inflationAmount) external onlyOwner {
        inflationAmount = _inflationAmount;
    }

    function canStartEpoch() external view returns (bool) {
        return
            inflationCount < 208 &&
            block.timestamp >=
            lastInflation +
                (testing ? testInflationInterval : inflationInterval);
    }

    function startEpoch() external onlyOwner {
        require(address(staking) != address(0), "Staking not set");
        // require(inflationCount < 208, "Maximum epoch");
        // require(
        //     block.timestamp >=
        //         lastInflation +
        //             (testing ? testInflationInterval : inflationInterval),
        //     "Last epoch not ended"
        // );

        inflationCount++;
        lastInflation = uint48(block.timestamp);

        if (inflationCount > 52) {
            inflationAmount -= (inflationAmount * inflationDecay) / 10000;
        }

        _mint(address(staking), inflationAmount);

        staking.distributeInflationRewards(inflationAmount);
    }
}
","keccak256":"0x3f2b822107652206d456162c03691046c9a6e7fd493e7cd3740b1107ce78265e","license":"UNLICENSED"},"contracts/staking/interfaces/ILendingPoolStaking.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

interface ILendingPoolStaking {
    error InsufficientBalance();
    error InvalidArgument(string details);
    error InvalidState();
    error Unauthorized();

    enum StakeStatus {
        DEFAULT, // Not staked, only applicable to escrowed reward
        STAKED, // Stake ongoing
        FULFILLED, // Stake ended gracefully
        CANCELED // Stake ended because of early unstake by user
    }

    enum EscrowStatus {
        DEFAULT, // Not issued
        ISSUED,
        CLAIMED,
        CANCELED // Canceled due to early unstake by user
    }

    struct Stake {
        // Staker address
        address staker;
        // Stake token address
        address token;
        // The time of deposit
        uint48 start;
        // The time of withdrawal
        uint48 end;
        // The amount staked by each stake duration
        uint256[3] amountsPerDuration;
        // The amount of stake token that will be rewarded upon finishing the stake duration
        uint256 rewardAllocated;
        // The idx of reward allocation to this stake
        uint8 rewardAllocationId;
        // Ids of child escrows
        uint256[] childIds;
        // Stake ids that created this stake as escrowed reward
        uint256[] parentIds;
        // Status of eab
        EscrowStatus escrowStatus;
        // Status of stake
        StakeStatus stakeStatus;
    }

    event Staked(
        uint256 stakeId,
        address staker,
        address token,
        uint48 start,
        uint48 end,
        uint256 amount,
        uint8 rewardAllocationId,
        uint256[] parentIds
    );
    event RewardsAllocated(uint256[] stakeIds, uint256[] rewardsAllocated);
    event StakeStatusChanged(uint256 stakeId, StakeStatus status);
    event EscrowStatusChanged(uint256 stakeId, EscrowStatus status);
    event RewardAllocationsChanged(uint8[3] allocations);

    function deposit(
        address _stakeToken,
        uint256 _amount,
        uint8 _durationId
    ) external payable;

    function stakeEscrowedReward(uint256 _stakeId) external ;

    function distributeInflationRewards(uint256 _inflationReward) external;

    function distributeNonInflationRewards() external;

    function claimEscrowedReward(uint256 _stakeId) external;

    function unstake(uint256 _stakeId) external;

    function addStakeToken(address _stakeToken) external;

    function removeStakeToken(address _stakeToken) external;
}
