","keccak256":"0xfa22c01bc01559b14a4c692d1c7ca3ccd71541fbf54ecebefcf0b5a5e46da466","license":"UNLICENSED"},"contracts/staking/LendingPoolStaking.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "../vault/Vault.sol";
import "../vault/BalanceNames.sol";
import "./interfaces/ILendingPoolStaking.sol";
import "../test/Testing.sol";
import "../access/SimpleRoleAccess.sol";
import "../access/MWOwnable.sol";

contract LendingPoolStaking is
    ILendingPoolStaking,
    ERC721URIStorage,
    BalanceNames,
    Testing,
    SimpleRoleAccess
{
    address public nend;
    Vault public lendingPool;
    mapping(address => bool) public activeStakeTokens;
    mapping(address => uint256) public inflationRollOver;
    address[] public stakeTokens;
    uint48 public escrowLockPeriod = 30 weeks;
    uint48 public testEscrowLockPeriod = 3 minutes;
    // Active stake token count
    uint256 public stakeTokenCount;
    // Active stake count
    uint256 public ongoingStakeCount;
    mapping(address => uint256) internal ifpTokenToAmount;
    // User address to this week's escrowed reward stake id
    mapping(address => mapping(uint48 => uint256)) userToTimeToEscrowId;
    // Token add => duration id => whether there is any stake receiving inflation reward with given duration and token
    mapping(address => mapping(uint8 => bool)) tokenRewardAllocationToActive;
    Stake[] public stakes;
    uint48[3] public stakeDurations = [1 hours, 4 hours, 12 hours];
    uint48[3] public testStakeDurations = [1 minutes, 4 minutes, 12 minutes];
    // Token address => duration id => amount
    mapping(address => mapping(uint8 => uint256))
        public totalStakedByToken_Duration;
    // User address => token address => duration id => amount
    mapping(address => mapping(address => mapping(uint8 => uint256)))
        public totalStakedByUser_Token_Duration;
    uint8[3] public rewardAllocations = [25, 35, 40];

    function setRewardAllocations(uint8[3] memory _rewardAllocations)
        external
        onlyRole("admin")
    {
        if (
            _rewardAllocations[0] +
                _rewardAllocations[1] +
                _rewardAllocations[2] !=
            100
        ) {
            revert InvalidArgument("Must sum up to 100");
        }
        rewardAllocations = _rewardAllocations;
        emit RewardAllocationsChanged(_rewardAllocations);
    }

    constructor(address _nend, Vault _lendingPool)
        ERC721("Escrowed Asset Bond", "EAB")
    {
        nend = _nend;
        lendingPool = _lendingPool;
        // Add native token
        activeStakeTokens[address(0)] = true;
        stakeTokens.push(address(0));
        stakeTokenCount++;

        // Add nend
        activeStakeTokens[_nend] = true;
        stakeTokens.push(_nend);
        stakeTokenCount++;
    }

    function deposit(
        address _token,
        uint256 _amount,
        uint8 _durationId
    ) external payable override {
        if (_amount == 0) {
            revert InvalidArgument("Amount cannot be zero");
        }
        if (!activeStakeTokens[_token]) {
            revert InvalidArgument("Invalid stake token");
        }

        bool isNativeCoin = _token == address(0);

        if (isNativeCoin) {
            if (msg.sender.balance < _amount) {
                revert InsufficientBalance();
            }
        } else {
            if (IERC20(_token).balanceOf(msg.sender) < _amount) {
                revert InsufficientBalance();
            }
        }

        if (isNativeCoin) {
            if (msg.value != _amount) {
                revert InvalidArgument("Incorrect native coin stake amount");
            }
            payable(address(lendingPool)).transfer(_amount);
        } else {
            IERC20(_token).transferFrom(
                msg.sender,
                address(lendingPool),
                _amount
            );
        }

        uint256[3] memory _amounts;
        _amounts[_durationId] = _amount;

        stakes.push(
            Stake(
                msg.sender,
                _token,
                uint48(block.timestamp),
                uint48(block.timestamp) +
                    (
                        testing
                            ? testStakeDurations[_durationId]
                            : stakeDurations[_durationId]
                    ),
                _amounts,
                0,
                _durationId,
                new uint256[](0),
                new uint256[](0),
                EscrowStatus.DEFAULT,
                StakeStatus.STAKED
            )
        );

        totalStakedByUser_Token_Duration[msg.sender][_token][
            _durationId
        ] += _amount;
        totalStakedByToken_Duration[_token][_durationId] += _amount;
        ongoingStakeCount++;

        _emitStaked(stakes.length);
    }

    function stakeEscrowedReward(uint256 _stakeId) external override {
        Stake storage _stake = stakes[_stakeId - 1];
        if (_stake.staker != msg.sender) {
            revert Unauthorized();
        }

        // Stake is not escrow or is already staked
        if (
            _stake.parentIds.length == 0 ||
            _stake.stakeStatus != StakeStatus.DEFAULT
        ) {
            revert InvalidState();
        }

        _stake.stakeStatus = StakeStatus.STAKED;
        ongoingStakeCount++;

        for (uint8 i = 0; i < 3; i++) {
            totalStakedByUser_Token_Duration[_stake.staker][_stake.token][
                _stake.rewardAllocationId
            ] += _stake.amountsPerDuration[i];
            totalStakedByToken_Duration[_stake.token][
                _stake.rewardAllocationId
            ] += _stake.amountsPerDuration[i];
        }

        emit StakeStatusChanged(_stakeId, _stake.stakeStatus);
    }

    function _compoundEscrow(uint256 _stakeId, uint256 _inflationReward)
        internal
    {
        Stake storage _stake = stakes[_stakeId - 1];

        if (userToTimeToEscrowId[_stake.staker][uint48(block.timestamp)] == 0) {
            uint256[3] memory _amounts;
            stakes.push(
                Stake(
                    _stake.staker,
                    nend,
                    uint48(block.timestamp),
                    uint48(block.timestamp) +
                        (testing ? testEscrowLockPeriod : escrowLockPeriod),
                    _amounts,
                    0,
                    2,
                    new uint256[](0),
                    new uint256[](0),
                    EscrowStatus.DEFAULT,
                    StakeStatus.DEFAULT
                )
            );
            userToTimeToEscrowId[_stake.staker][
                uint48(block.timestamp)
            ] = stakes.length;
        }

        _stake.childIds.push(
            userToTimeToEscrowId[_stake.staker][uint48(block.timestamp)]
        );
        Stake storage _escrow = stakes[
            userToTimeToEscrowId[_stake.staker][uint48(block.timestamp)] - 1
        ];
        _escrow.parentIds.push(_stakeId);

        uint256 reward = _calculateReward(
            _stake.token,
            _stake.staker,
            _stake.rewardAllocationId,
            _inflationReward / stakeTokenCount + inflationRollOver[_stake.token]
        );

        _escrow.amountsPerDuration[_stake.rewardAllocationId] += reward;
        tokenRewardAllocationToActive[_stake.token][
            _stake.rewardAllocationId
        ] = true;
    }

    function _accrueNonInflationReward(
        uint256 _stakeId,
        uint256 _nonInflationReward
    ) internal {
        Stake storage _stake = stakes[_stakeId - 1];

        uint256 reward = _calculateReward(
            _stake.token,
            _stake.staker,
            _stake.rewardAllocationId,
            _nonInflationReward
        );

        _stake.rewardAllocated += reward;
    }

    function distributeInflationRewards(uint256 _inflationReward)
        external
        override
    {
        if (msg.sender != nend) {
            revert Unauthorized();
        }

        // Deal with list being modified during loop
        uint256 _stakeCount = stakes.length;

        for (uint256 i = 0; i < _stakeCount; i++) {
            Stake storage _stake = stakes[i];

            if (
                _stake.parentIds.length != 0 ||
                _stake.stakeStatus != StakeStatus.STAKED
            ) {
                continue;
            }

            _compoundEscrow(i + 1, _inflationReward);
        }

        for (uint256 i = _stakeCount; i < stakes.length; i++) {
            _emitStaked(i + 1);
        }

        // Calculate rollover
        for (uint8 i = 0; i < stakeTokens.length; i++) {
            address _stakeToken = stakeTokens[i];
            uint256 rollOver = inflationRollOver[_stakeToken];
            inflationRollOver[_stakeToken] = 0;
            for (uint8 j = 0; j < 3; j++) {
                if (!tokenRewardAllocationToActive[_stakeToken][j]) {
                    inflationRollOver[_stakeToken] +=
                        ((_inflationReward / stakeTokenCount + rollOver) *
                            rewardAllocations[j]) /
                        100;
                }
                delete tokenRewardAllocationToActive[_stakeToken][j];
            }
        }
    }

    function distributeNonInflationRewards() external override onlyOwner {
        for (uint256 i = 0; i < stakeTokens.length; i++) {
            ifpTokenToAmount[stakeTokens[i]] = lendingPool.getNamedBalance(
                interestForPoolBalanceName,
                stakeTokens[i]
            );
        }

        uint256[] memory stakeIds = new uint256[](ongoingStakeCount);
        uint256[] memory rewardsAllocated = new uint256[](ongoingStakeCount);
        uint256 idx;

        for (uint256 i = 0; i < stakes.length; i++) {
            Stake storage _stake = stakes[i];

            if (_stake.stakeStatus != StakeStatus.STAKED) {
                continue;
            }

            _accrueNonInflationReward(i + 1, ifpTokenToAmount[_stake.token]);

            stakeIds[idx] = i + 1;
            rewardsAllocated[idx] = _stake.rewardAllocated;

            idx++;
        }

        emit RewardsAllocated(stakeIds, rewardsAllocated);
    }

    function issueEAB(uint256 _stakeId) external {
        Stake storage _stake = stakes[_stakeId - 1];
        if (_stake.staker != msg.sender) {
            revert Unauthorized();
        }
        if (_stake.escrowStatus != EscrowStatus.DEFAULT) {
            revert InvalidState();
        }
        for (uint8 i = 0; i < _stake.parentIds.length; i++) {
            // Parent eab already issued
            if (
                stakes[_stake.parentIds[i] - 1].escrowStatus !=
                EscrowStatus.DEFAULT
            ) {
                {
                    revert InvalidState();
                }
            }
        }
        for (uint8 i = 0; i < _stake.childIds.length; i++) {
            // Child eab already issued
            if (
                stakes[_stake.childIds[i] - 1].escrowStatus !=
                EscrowStatus.DEFAULT
            ) {
                {
                    revert InvalidState();
                }
            }
        }
        _stake.escrowStatus = EscrowStatus.ISSUED;
        _mint(msg.sender, _stakeId);

        emit EscrowStatusChanged(_stakeId, EscrowStatus.ISSUED);
    }

    function claimEscrowedReward(uint256 _stakeId) external override {
        Stake storage _stake = stakes[_stakeId - 1];
        if (_stake.parentIds.length == 0 || _stake.escrowStatus == EscrowStatus.CLAIMED || _stake.escrowStatus == EscrowStatus.CANCELED || _stake.end > block.timestamp) {
            revert InvalidState();
        }
        if (_stake.staker != msg.sender) {
            revert Unauthorized();
        }

        _stake.escrowStatus = EscrowStatus.CLAIMED;

        if (_stake.stakeStatus == StakeStatus.STAKED) {
            _unstake(_stakeId, StakeStatus.FULFILLED);
        }

        if (_exists(_stakeId)) {
            _burn(_stakeId);
        }

        IERC20(nend).transfer(
            msg.sender,
            _stake.amountsPerDuration[0] +
                _stake.amountsPerDuration[1] +
                _stake.amountsPerDuration[2]
        );

        emit EscrowStatusChanged(_stakeId, EscrowStatus.CLAIMED);
    }

    function unstake(uint256 _stakeId) external override {
        Stake storage _stake = stakes[_stakeId - 1];
        if (_stake.staker != msg.sender) {
            revert Unauthorized();
        }
        if (_stake.stakeStatus != StakeStatus.STAKED) {
            revert InvalidState();
        }
        for (uint8 i = 0; i < _stake.childIds.length; i++) {
            // Child eab sold
            if (stakes[_stake.childIds[i] - 1].staker != _stake.staker) {
                {
                    revert InvalidState();
                }
            }
        }

        uint256 _amount = _stake.amountsPerDuration[0] +
            _stake.amountsPerDuration[1] +
            _stake.amountsPerDuration[2];

        if (_stake.token != address(0)) {
            if (
                IERC20(_stake.token).balanceOf(address(lendingPool)) < _amount
            ) {
                revert InsufficientBalance();
            }
        } else {
            if (address(lendingPool).balance >= _amount) {
                revert InsufficientBalance();
            }
        }

        _lendingPoolTransfer(_stake.token, msg.sender, _amount);

        if (block.timestamp > _stake.end) {
            return;
        }

        for (uint256 i = 0; i < _stake.childIds.length; i++) {
            Stake storage _childStake = stakes[_stake.childIds[i] - 1];
            if (_exists(_stake.childIds[i])) {
                _burn(_stake.childIds[i]);
            }

            if (_childStake.stakeStatus == StakeStatus.STAKED) {
                _unstake(_stake.childIds[i], StakeStatus.CANCELED);
            }

            _lendingPoolTransfer(
                nend,
                owner(),
                _amount
            );
        }

        _unstake(_stakeId, StakeStatus.CANCELED);
    }

    function _unstake(uint256 _stakeId, StakeStatus _status) internal {
        Stake storage _stake = stakes[_stakeId - 1];

        _stake.stakeStatus = _status;
        ongoingStakeCount--;

        for (uint8 i = 0; i < 3; i++) {
            totalStakedByUser_Token_Duration[_stake.staker][_stake.token][
                _stake.rewardAllocationId
            ] -= _stake.amountsPerDuration[i];
            totalStakedByToken_Duration[_stake.token][
                _stake.rewardAllocationId
            ] -= _stake.amountsPerDuration[i];
        }

        emit StakeStatusChanged(_stakeId, _status);

        if (
            _status == StakeStatus.CANCELED &&
            _stake.escrowStatus == EscrowStatus.ISSUED
        ) {
            _stake.escrowStatus = EscrowStatus.CANCELED;
            emit EscrowStatusChanged(_stakeId, EscrowStatus.CANCELED);
        }
    }

    function addStakeToken(address _stakeToken)
        external
        override
        onlyRole("admin")
    {
        if (!activeStakeTokens[_stakeToken]) {
            stakeTokenCount++;
            activeStakeTokens[_stakeToken] = true;
        }

        for (uint8 i = 0; i < stakeTokens.length; i++) {
            if (stakeTokens[i] == _stakeToken) {
                return;
            }
        }

        stakeTokens.push(_stakeToken);
    }

    function removeStakeToken(address _stakeToken)
        external
        override
        onlyRole("admin")
    {
        if (activeStakeTokens[_stakeToken]) {
            stakeTokenCount--;
            activeStakeTokens[_stakeToken] = false;
        }
    }

    function setTokenURI(uint256 _tokenId, string memory _tokenURI)
        external
        onlyOwner
    {
        _setTokenURI(_tokenId, _tokenURI);
    }

    function _lendingPoolTransfer(
        address _token,
        address _to,
        uint256 _amount
    ) internal {
        bool isNativeCoin = _token == address(0);

        if (isNativeCoin) {
            if (address(lendingPool).balance < _amount) {
                revert InsufficientBalance();
            }
            lendingPool.transferNative(payable(_to), _amount);
        } else {
            if (IERC20(_token).balanceOf(address(lendingPool)) < _amount) {
                revert InsufficientBalance();
            }
            lendingPool.transferERC20(_token, _to, _amount);
        }
    }

    function _calculateReward(
        address _stakeToken,
        address _staker,
        uint8 _rewardAllocationId,
        uint256 _reward
    ) internal view returns (uint256) {
        return
            (_reward *
                rewardAllocations[_rewardAllocationId] *
                totalStakedByUser_Token_Duration[_staker][_stakeToken][
                    _rewardAllocationId
                ]) /
            100 /
            totalStakedByToken_Duration[_stakeToken][_rewardAllocationId];
    }

    function _emitStaked(uint256 _stakeId) internal {
        Stake memory _stake = stakes[_stakeId - 1];
        emit Staked(
            _stakeId,
            _stake.staker,
            _stake.token,
            _stake.start,
            _stake.end,
            _stake.amountsPerDuration[0] +
                _stake.amountsPerDuration[1] +
                _stake.amountsPerDuration[2],
            _stake.rewardAllocationId,
            _stake.parentIds
        );
    }
}
","keccak256":"0x0d38ed741c14a1f9d77ac5b8ce4b03c1ec6ceb9e5d4637d8b2bedd495e2a8157","license":"UNLICENSED"},"contracts/staking/interfaces/ILendingPoolStaking.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

interface ILendingPoolStaking {
    error InsufficientBalance();
    error InvalidArgument(string details);
    error InvalidState();
    error Unauthorized();

    enum StakeStatus {
        DEFAULT, // Not staked, only applicable to escrowed reward
        STAKED, // Stake ongoing
        FULFILLED, // Stake ended gracefully
        CANCELED // Stake ended because of early unstake by user
    }

    enum EscrowStatus {
        DEFAULT, // Not issued
        ISSUED,
        CLAIMED,
        CANCELED // Canceled due to early unstake by user
    }

    struct Stake {
        // Staker address
        address staker;
        // Stake token address
        address token;
        // The time of deposit
        uint48 start;
        // The time of withdrawal
        uint48 end;
        // The amount staked by each stake duration
        uint256[3] amountsPerDuration;
        // The amount of stake token that will be rewarded upon finishing the stake duration
        uint256 rewardAllocated;
        // The idx of reward allocation to this stake
        uint8 rewardAllocationId;
        // Ids of child escrows
        uint256[] childIds;
        // Stake ids that created this stake as escrowed reward
        uint256[] parentIds;
        // Status of eab
        EscrowStatus escrowStatus;
        // Status of stake
        StakeStatus stakeStatus;
    }

    event Staked(
        uint256 stakeId,
        address staker,
        address token,
        uint48 start,
        uint48 end,
        uint256 amount,
        uint8 rewardAllocationId,
        uint256[] parentIds
    );
    event RewardsAllocated(uint256[] stakeIds, uint256[] rewardsAllocated);
    event StakeStatusChanged(uint256 stakeId, StakeStatus status);
    event EscrowStatusChanged(uint256 stakeId, EscrowStatus status);
    event RewardAllocationsChanged(uint8[3] allocations);

    function deposit(
        address _stakeToken,
        uint256 _amount,
        uint8 _durationId
    ) external payable;

    function stakeEscrowedReward(uint256 _stakeId) external ;

    function distributeInflationRewards(uint256 _inflationReward) external;

    function distributeNonInflationRewards() external;

    function claimEscrowedReward(uint256 _stakeId) external;

    function unstake(uint256 _stakeId) external;

    function addStakeToken(address _stakeToken) external;

    function removeStakeToken(address _stakeToken) external;
}
