        mapping(address => mapping(uint256 => uint256)) storage _userIndexToId,
        uint256 stakeId,
        address nend
    ) external returns (bool migrated) {
        ILendingPoolStakingV2.Stake storage stake = stakes[stakeId];

        if (
            stake.staker != address(0) &&
            stake.stakeStatus != ILendingPoolStakingV2.StakeStatus.FULFILLED
        ) {
            // Skip if already migrated
            if (_stakeEntries[stakeId].exists) {
                return false;
            }

            // Create a memory copy of the stake
            ILendingPoolStakingV2.Stake memory stakeCopy = stakes[stakeId];

            // Modify the token if it's an escrow stake
            if (stakeCopy.isEscrow) {
                stakeCopy.token = nend;
            }

            // Get the next available user stake index
            uint256 userStakeIdx = userStakesCount[stakeCopy.staker] + 1;

            // Store the modified copy, not the original storage reference
            userSpecificStakes[userStakeIdx] = stakeCopy;

            // Create mapping entry
            setStakeMapping(
                _stakeEntries,
                _userIndexToId,
                stakeId,
                stakeCopy.staker,
                userStakeIdx
            );

            userStakesCount[stakeCopy.staker]++;
            return true;
        }
        return false;
    }

    /**
     * @notice Complete implementation of getClaimableRewards
     * @dev Moved entirely from main contract to reduce bytecode
     */
    function getClaimableRewards(
        mapping(uint256 => ILendingPoolStakingV2.Stake)
            storage userSpecificStakes,
        uint256 userStakeCount,
        ILendingPoolStakingV2.RewardPeriod[2] storage _recentPeriods,
        mapping(address => mapping(uint64 => bool))
            storage _userClaimedForPeriod,
        mapping(address => mapping(uint8 => uint256))
            storage totalStakedByToken_Duration,
        uint256 _currentPeriodId,
        address nend,
        address _user,
        address _token
    ) external view returns (uint256 inflationReward, uint256 ifpReward) {
        // Get the current period (the one users can claim from)
        //
        ILendingPoolStakingV2.RewardPeriod storage period = _recentPeriods[
            _currentPeriodId
        ];

        // If the user already claimed for this period, return zeros
        if (_userClaimedForPeriod[_user][period.periodId]) {
            return (0, 0);
        }

        // Get total staked amount for this token
        uint256 totalTokenStaked = getTotalStakesForToken(
            totalStakedByToken_Duration,
            _token
        );

        // If nothing staked, no rewards
        if (totalTokenStaked == 0) {
            return (0, 0);
        }

        // Get user's total stake for this token
        uint256 userStakedAmount = calculateUserStakesTotal(
            userSpecificStakes,
            userStakeCount,
            _token,
            nend
        );

        if (userStakedAmount == 0) {
            return (0, 0);
        }

        // Calculate proportional rewards
        inflationReward =
            (period.rewardsToDistribute * userStakedAmount) /
            totalTokenStaked;
        ifpReward =
            (period.ifpRewardToDistribute * userStakedAmount) /
            totalTokenStaked;

        return (inflationReward, ifpReward);
    }

    /**
     * @notice Complete implementation of claim function logic
     * @dev Moved entirely from main contract to reduce bytecode
     */
    function processClaim(
        mapping(uint256 => ILendingPoolStakingV2.Stake)
            storage userSpecificStakes,
        mapping(address => mapping(uint8 => uint256))
            storage totalStakedByToken_Duration,
        uint256 userStakeCount,
        mapping(address => mapping(uint64 => bool))
            storage _userClaimedForPeriod,
        ILendingPoolStakingV2.RewardPeriod storage period,
        address _token,
        address nend,
        address user
    )
        external
        returns (uint256 inflationReward, uint256 ifpReward, uint64 periodId)
    {
        // Check if already claimed
        if (_userClaimedForPeriod[user][period.periodId]) {
            revert ILendingPoolStakingV2.AlreadyClaimed();
        }

        // Get total staked amount for this token
        uint256 totalTokenStakeAmount = getTotalStakesForToken(
            totalStakedByToken_Duration,
            _token
        );

        // If nothing staked, no rewards
        if (totalTokenStakeAmount == 0) {
            revert ILendingPoolStakingV2.NoStakeYet();
        }

        // Get user's total stake for this token
        uint256 userStakeAmount = calculateUserStakesTotal(
            userSpecificStakes,
            userStakeCount,
            _token,
            nend
        );

        if (userStakeAmount == 0) {
            revert ILendingPoolStakingV2.StakeNotFound();
        }

        // Calculate proportional rewards
        inflationReward =
            (period.rewardsToDistribute * userStakeAmount) /
            totalTokenStakeAmount;
        ifpReward =
            (period.ifpRewardToDistribute * userStakeAmount) /
            totalTokenStakeAmount;

        if (inflationReward == 0 && ifpReward == 0) {
            revert ILendingPoolStakingV2.NoRewardsAvailable();
        }

        // Update period totals
        period.rewardsStaked += inflationReward;
        period.ifpRewardClaimed += ifpReward;

        periodId = period.periodId;

        // Mark as claimed for this period
        _userClaimedForPeriod[user][period.periodId] = true;

        return (inflationReward, ifpReward, periodId);
    }

    /**
     * @notice Process unstake operation completely
     * @dev Moved from main contract with all logic intact
     */
    function processUnstake(
        ILendingPoolStakingV2.Stake storage stake,
        mapping(address => mapping(uint8 => uint256))
            storage totalStakedByToken_Duration,
        address nend,
        address msgSender
    )
        external
        returns (
            uint256 stakedAmount,
            uint256 rewardAmount,
            address tokenToUse,
            bool needsBurn
        )
    {
        // Validate
        if (stake.staker != msgSender) {
            revert ILendingPoolStakingV2.Unauthorized();
        }

        if (
            stake.end > block.timestamp ||
            stake.stakeStatus == ILendingPoolStakingV2.StakeStatus.FULFILLED ||
            stake.escrowStatus == ILendingPoolStakingV2.EscrowStatus.CLAIMED
        ) {
            revert ILendingPoolStakingV2.InvalidState();
        }

        // Calculate amount to return
        unchecked {
            stakedAmount =
                stake.amountsPerDuration[0] +
                stake.amountsPerDuration[1] +
                stake.amountsPerDuration[2];
        }

        // Get reward and token
        rewardAmount = stake.rewardAllocated;
        tokenToUse = stake.isEscrow ? nend : stake.token;

        // Update totals if staked
        if (stake.stakeStatus == ILendingPoolStakingV2.StakeStatus.STAKED) {
            for (uint8 i = 0; i < 3; ) {
                totalStakedByToken_Duration[
                    stake.isEscrow ? nend : stake.token
                ][i] -= stake.amountsPerDuration[i];
                unchecked {
                    ++i;
                }
            }
        }

        // Update state
        stake.stakeStatus = ILendingPoolStakingV2.StakeStatus.FULFILLED;

        // Check if burn needed
        needsBurn =
            stake.escrowStatus == ILendingPoolStakingV2.EscrowStatus.ISSUED ||
            stake.isEscrow;

        if (needsBurn) {
            stake.escrowStatus = ILendingPoolStakingV2.EscrowStatus.CLAIMED;
        }

        return (stakedAmount, rewardAmount, tokenToUse, needsBurn);
    }

    /**
     * @notice Complete implementation of closing the current period
     */
    function closeCurrentPeriod(
        ILendingPoolStakingV2.RewardPeriod[2] storage _recentPeriods,
        uint256 _currentPeriodId,
        uint256 toDistributeReward,
        uint256 ifptoDistributeReward
    ) external returns (uint256 newCurrentPeriod) {
        // get new current period index(= previous period != current period)
        // when old current period is 0, next period is 1
        // when old current period is 1, next period is 0
        newCurrentPeriod =
            (_currentPeriodId + REWARD_PERIOD_LENGTH - 1) %
            REWARD_PERIOD_LENGTH;

        // Clear old data
        delete _recentPeriods[newCurrentPeriod];

        // Set up new period
        _recentPeriods[newCurrentPeriod].periodId =
            _recentPeriods[_currentPeriodId].periodId +
            1;
        _recentPeriods[newCurrentPeriod].startTime = uint64(block.timestamp);
        _recentPeriods[newCurrentPeriod]
            .rewardsToDistribute = toDistributeReward;
        _recentPeriods[newCurrentPeriod]
            .ifpRewardToDistribute = ifptoDistributeReward;

        return (newCurrentPeriod);
    }

    /**
     * @notice Create escrow stake with complete logic
     */
    function setStakeData(
        ILendingPoolStakingV2.Stake storage stake,
        address _staker,
        uint256[3] memory _amounts,
        address _token,
        uint48 lockPeriod,
        bool isEscrow,
        bool testing
    ) public {
        // Get lock period for escrow
        uint48 start = uint48(block.timestamp);

        stake.staker = _staker;
        stake.token = _token;
        stake.start = start;
        stake.end = start + lockPeriod / (testing ? 1008 : 1);
        stake.amountsPerDuration = _amounts;
        stake.rewardAllocated = 0;
        stake.isEscrow = isEscrow;
        stake.escrowStatus = ILendingPoolStakingV2.EscrowStatus.DEFAULT;
        stake.stakeStatus = ILendingPoolStakingV2.StakeStatus.STAKED;
    }

    /**
     * @notice Calculates total staked amount by a user for a specific token
     */
    function calculateUserStakesTotal(
        mapping(uint256 => ILendingPoolStakingV2.Stake)
            storage userSpecificStakes,
        uint256 userStakesCount,
        address _token,
        address nend
    ) public view returns (uint256 totalAmount) {
        for (uint256 i = 0; i < userStakesCount; ) {
            // Using storage to avoid unnecessary copying
            ILendingPoolStakingV2.Stake storage stake = userSpecificStakes[
                i + 1
            ];

            // Only include active stakes for the specific token
            if (stake.stakeStatus == ILendingPoolStakingV2.StakeStatus.STAKED) {
                // Check if the token matches:
                // 1. For escrow stakes, we use nend token
                // 2. For regular stakes, use the actual token
                bool isMatchingToken = stake.isEscrow
                    ? (_token == nend)
                    : (stake.token == _token);

                if (isMatchingToken) {
                    // Sum up all durations in unchecked block
                    unchecked {
                        totalAmount +=
                            stake.amountsPerDuration[0] +
                            stake.amountsPerDuration[1] +
                            stake.amountsPerDuration[2];
                    }
                }
            }

            unchecked {
                ++i;
            }
        }
    }

    /**
     * @notice Gets total staked amount for a token across all durations
     */
    function getTotalStakesForToken(
        mapping(address => mapping(uint8 => uint256))
            storage totalStakedByToken_Duration,
        address _token
    ) public view returns (uint256 totalStaked) {
        unchecked {
            totalStaked =
                totalStakedByToken_Duration[_token][0] +
                totalStakedByToken_Duration[_token][1] +
                totalStakedByToken_Duration[_token][2];
        }
    }

    /**
     * @notice Calculates pool rollover amounts from previous period
     */
    function calculatePoolRollOver(
        ILendingPoolStakingV2.RewardPeriod storage _currentPeriod
    )
        external
        view
        returns (uint256 poolRewardRemained, uint256 ifpPoolRewardRemained)
    {
        // Calculate remaining rewards
        poolRewardRemained =
            _currentPeriod.rewardsToDistribute -
            _currentPeriod.rewardsStaked;

        ifpPoolRewardRemained =
            _currentPeriod.ifpRewardToDistribute -
            _currentPeriod.ifpRewardClaimed;
    }

    /**
     * @notice Safely transfers assets from lending pool to recipient
     */
    function lendingPoolTransfer(
        Vault lendingPool,
        address _token,
        address _to,
        uint256 _amount
    ) external {
        bool isNativeCoin = _token == address(0);

        if (isNativeCoin) {
            require(
                address(lendingPool).balance >= _amount,
                "Insufficient balance"
            );
            lendingPool.transferNative(payable(_to), _amount);
        } else {
            require(
                IERC20(_token).balanceOf(address(lendingPool)) >= _amount,
                "Insufficient balance"
            );
            lendingPool.transferERC20(_token, _to, _amount);
        }
    }

    function setStakeMapping(
        mapping(uint256 => ILendingPoolStakingV2.StakeMappingEntry)
            storage _stakeEntries,
        mapping(address => mapping(uint256 => uint256)) storage _userIndexToId,
        uint256 _stakeId,
        address _user,
        uint256 _userIndex
    ) public {
        // Create and store the mapping entry
        _stakeEntries[_stakeId] = ILendingPoolStakingV2.StakeMappingEntry({
            user: _user,
            userIndex: _userIndex,
            stakeId: _stakeId,
            exists: true
        });

        // Update the reverse lookup
        _userIndexToId[_user][_userIndex] = _stakeId;
    }

    function removeUserStake(
        mapping(uint256 => ILendingPoolStakingV2.Stake)
            storage userSpecificStakes,
        mapping(uint256 => ILendingPoolStakingV2.StakeMappingEntry)
            storage _stakeEntries,
        mapping(address => mapping(uint256 => uint256)) storage _userIndexToId,
        mapping(address => uint256) storage userStakesCount,
        address _user,
        uint256 _stakeId,
        uint256 _userIndex
    ) external {
        // Get the last stake index for this user
        uint256 lastUserStakeIdx = userStakesCount[_user];

        // If not the last element, swap with the last element
        if (_userIndex != lastUserStakeIdx) {
            // Get the last stake
            ILendingPoolStakingV2.Stake storage lastStake = userSpecificStakes[
                lastUserStakeIdx
            ];

            // Get last stake ID using the reverse mapping
            uint256 lastStakeId = _userIndexToId[_user][lastUserStakeIdx];

            // Move last stake to current position
            userSpecificStakes[_userIndex] = lastStake;

            // Update the stake ID mapping for the moved stake
            setStakeMapping(
                _stakeEntries,
                _userIndexToId,
                lastStakeId,
                _user,
                _userIndex
            );
        }

        // Clean up the last position
        delete userSpecificStakes[lastUserStakeIdx];

        // Delete the original stake mapping
        delete _userIndexToId[_user][lastUserStakeIdx];
        delete _stakeEntries[_stakeId];

        // Decrement the user's stake count
        userStakesCount[_user]--;
    }

    function _saveStakedRewards(
        mapping(address => mapping(uint8 => uint256))
            storage totalStakedByToken_Duration,
        address nend,
        uint256[3] memory _amounts
    ) internal {
        for (uint8 i = 0; i < 3; ) {
            totalStakedByToken_Duration[nend][i] += _amounts[i];
            unchecked {
                ++i;
            }
        }
    }

    function _createAndMapStake(
        mapping(uint256 => ILendingPoolStakingV2.Stake)
            storage userSpecificStakes,
        address _staker,
        uint256[3] memory _amounts,
        address _token,
        uint48 _duration,
        bool _isEscrow,
        uint256 stakeId,
        mapping(address => uint256) storage userStakesCount,
        mapping(uint256 => ILendingPoolStakingV2.StakeMappingEntry)
            storage _stakeEntries,
        mapping(address => mapping(uint256 => uint256)) storage _userIndexToId,
        bool testing
    ) internal returns (uint256) {
        // Add to user's stakes
        uint256 userStakeIdx = 1 + userStakesCount[_staker];
        userStakesCount[_staker]++;
        // Directly initialize the stake in storage
        ILendingPoolStakingV2.Stake storage newStake = userSpecificStakes[
            userStakeIdx
        ];
        setStakeData(
            newStake,
            _staker,
            _amounts,
            _token,
            _duration,
            _isEscrow,
            testing
        );

        // Map the stake ID to the user and index
        setStakeMapping(
            _stakeEntries,
            _userIndexToId,
            stakeId,
            _staker,
            userStakeIdx
        );

        return userStakeIdx;
    }
}
","keccak256":"0xa0dfbb217a1958c98e059507659b8ea92149ba3b771ee8d8b9e5cc3f0d98c548","license":"UNLICENSED"},"contracts/staking/interfaces/ILendingPoolStakingV2.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

interface ILendingPoolStakingV2 {

    error InsufficientBalance();
    error InvalidArgument(string details);
    error InvalidState();
    error Unauthorized();
    error AlreadyClaimed();
    error NoRewardsAvailable();
    error StakeNotFound();
    error NotOwned();
    error NoStakeYet();

    enum StakeStatus {
        DEFAULT, // Not staked, only applicable to escrowed reward
        STAKED, // Stake ongoing
        FULFILLED // Stake ended gracefully
    }

    enum EscrowStatus {
        DEFAULT, // Not issued
        ISSUED,
        CLAIMED
    }

    struct RewardPeriod {
        uint64 periodId;
        uint64 startTime;
        uint256 rewardsToDistribute;
        uint256 rewardsStaked;
        uint256 ifpRewardToDistribute;
        uint256 ifpRewardClaimed;
    }

    /**
     * @notice Combined stake mapping data structure
     * @dev Used to optimize storage by consolidating bidirectional mappings
     */
    struct StakeMappingEntry {
        address user;        // Owner of the stake
        uint256 userIndex;   // Index in user's personal mapping
        uint256 stakeId;     // Global stake ID
        bool exists;         // Flag to confirm entry exists
    }

    struct Stake {
        // Staker address
        address staker;
        // Stake token address
        address token;
        // The time of deposit
        uint48 start;
        // The time of withdrawal
        uint48 end;
        // The amount staked by each stake duration
        uint256[3] amountsPerDuration;
        // The amount of stake token that will be rewarded upon finishing the stake duration
        uint256 rewardAllocated;
        // Stake is escrow
        bool isEscrow;
        // Status of eab
        EscrowStatus escrowStatus;
        // Status of stake
        StakeStatus stakeStatus;
    }

    event Staked(
        uint256 stakeId,
        address staker,
        address token,
        uint48 start,
        uint48 end,
        uint256[3] amountsPerDuration,
        bool isEscrow
    );
    event StakeStatusChanged(uint256 stakeId, StakeStatus status);
    event EscrowStatusChanged(uint256 stakeId, EscrowStatus status);
    event InflationRewardDistributed();
    event NonInflationRewardDistributed();
    // event ImplementationUpgraded(address indexed newImplementation);
    event BatchMigrationCompleted(uint256 startId, uint256 endId);

    event NewPeriodStarted(
        uint64 periodId,
        uint64 timestamp,
        uint256 inflationRewards,
        uint256 ifpRewards
    );

    event RewardsClaimed(
        address indexed user,
        address indexed token,
        uint256 inflationReward,
        uint256 ifpReward,
        uint64 periodId
    );
    function deposit(
        address _stakeToken,
        uint256 _amount,
        uint8 _durationId
    ) external payable;

    function stakeEscrowedReward(uint256 _stakeId) external;

    function distributeInflationRewards(uint256 _inflationReward) external;

    function hasPendingNonInflationRewards() external view returns (bool);

    function unstake(uint256 _stakeId) external;

    function addStakeToken(address _stakeToken) external;

    function removeStakeToken(address _stakeToken) external;


}","keccak256":"0x97cde0d96df6204cc50fd24dd0ffef592623d13ee2a3be8d8cfa9a02477d51ed","license":"UNLICENSED"},"contracts/vault/Vault.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "../helpers/SignatureHelper.sol";
import "../access/SimpleRoleAccess.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

contract Vault is SimpleRoleAccess, UUPSUpgradeable {
    using SignatureHelper for bytes32;

    event Claimed(bytes _hash);

    bytes4 private ERC1155_INTERFACE_ID;
    string public name;

    mapping(bytes => bool) public claimed;
    // Balance name => token => amount
    mapping(string => mapping(address => uint256)) public namedBalances;

    function initialize(string memory _name) public virtual initializer {
        __Vault_init(_name);
        __MWOwnable_init();
    }

    function __Vault_init(
        string memory _name
    ) internal virtual onlyInitializing {
        name = _name;
        ERC1155_INTERFACE_ID = 0xd9b67a26;
    }

    function approveERC20Transfer(
        address _tokenAddress,
        address _spender,
        uint256 _amount
    ) external virtual onlyRole("spender") returns (bool) {
        IERC20 erc20 = IERC20(_tokenAddress);
        return erc20.approve(_spender, _amount);
    }

    function transferERC20(
        address _tokenAddress,
        address _to,
        uint256 _amount
    ) external virtual onlyRole("spender") returns (bool) {
        IERC20 erc20 = IERC20(_tokenAddress);
        return erc20.transfer(_to, _amount);
    }

    function setERC721ApprovalForAll(
        address _tokenAddress,
        address _operator,
        bool _approved
    ) external virtual onlyRole("spender") {
        IERC721 erc721 = IERC721(_tokenAddress);
        erc721.setApprovalForAll(_operator, _approved);
    }

    function transferERC721(
        address _tokenAddress,
        address _to,
        uint256 _tokenId
    ) external virtual onlyRole("spender") {
        IERC721 erc721 = IERC721(_tokenAddress);
        erc721.transferFrom(address(this), _to, _tokenId);
    }

    function transferERC1155(
        address _tokenAddress,
        address _to,
        uint256 _id,
        uint256 _value,
        bytes calldata _data
    ) external virtual onlyRole("spender") {
        IERC1155 erc1155 = IERC1155(_tokenAddress);
        require(
            erc1155.supportsInterface(ERC1155_INTERFACE_ID),
            "given token address doesn't support ERC1155"
        );
        erc1155.safeTransferFrom(address(this), _to, _id, _value, _data);
    }

    function transferERC1155Batch(
        address _tokenAddress,
        address _to,
        uint256[] calldata _ids,
        uint256[] calldata _values,
        bytes calldata _data
    ) external virtual onlyRole("spender") {
        IERC1155 erc1155 = IERC1155(_tokenAddress);
        require(
            erc1155.supportsInterface(ERC1155_INTERFACE_ID),
            "given token address doesn't support ERC1155"
        );
        erc1155.safeBatchTransferFrom(address(this), _to, _ids, _values, _data);
    }

    function setERC1155ApprovalForAll(
        address _tokenAddress,
        address _operator,
        bool _approved
    ) external virtual onlyRole("spender") {
        IERC1155 erc1155 = IERC1155(_tokenAddress);
        require(
            erc1155.supportsInterface(ERC1155_INTERFACE_ID),
            "given token address doesn't support ERC1155"
        );
        erc1155.setApprovalForAll(_operator, _approved);
    }

    function getNativeBalance() external view virtual returns (uint256) {
        return address(this).balance;
    }

    function transferNative(
        address payable _to,
        uint256 _amount
    ) public payable virtual onlyRole("spender") {
        (bool sent, ) = _to.call{ value: _amount }("");
        require(sent, "Failed to send Ether");
    }

    function burn(
        address _token,
        uint256 _amount
    ) public virtual onlyRole("spender") {
        ERC20Burnable(_token).burn(_amount);
    }

    function namedBalanceReceive(
        string memory _name,
        address _token,
        uint256 _amount
    ) external virtual onlyRole("spender") {
        namedBalances[_name][_token] += _amount;
    }

    function namedBalanceSpend(
        string memory _name,
        address _token,
        uint256 _amount
    ) external virtual onlyRole("spender") {
        require(
            namedBalances[_name][_token] >= _amount,
            "Insufficient balance"
        );
        namedBalances[_name][_token] -= _amount;
    }

    function getNamedBalance(
        string memory _name,
        address _token
    ) external view virtual returns (uint256) {
        uint256 balance = namedBalances[_name][_token];
        uint256 actualBalance = _token == address(0)
            ? payable(this).balance
            : IERC20(_token).balanceOf(address(this));

        return balance <= actualBalance ? balance : actualBalance;
    }

    function claim(
        bytes[] memory _hashes,
        bytes[] memory _signatures
    ) external virtual {
        require(
            _hashes.length == _signatures.length,
            "Not matching hash and signature count"
        );

        for (uint256 i = 0; i < _hashes.length; i++) {
            if (claimed[_hashes[i]]) {
                continue;
            }

            (
                address _recipientAddress,
                address _tokenAddress,
                uint256 _amount,
                uint48 _timestamp
            ) = abi.decode(_hashes[i], (address, address, uint256, uint48));

            bytes32 _messageHash = keccak256(
                abi.encodePacked(
                    _recipientAddress,
                    _tokenAddress,
                    _amount,
                    _timestamp
                )
            );

            require(
                _messageHash.recoverSigner(_signatures[i]) == owner(),
                "Invalid signature"
            );

            require(_recipientAddress == msg.sender, "Not recipient");
            require(block.timestamp >= _timestamp, "Invalid timestamp");
            require(
                IERC20(_tokenAddress).balanceOf(address(this)) >= _amount,
                "Insufficient pool balance"
            );

            claimed[_hashes[i]] = true;
            IERC20(_tokenAddress).transfer(msg.sender, _amount);
            emit Claimed(_hashes[i]);
        }
    }

    receive() external payable {}

    fallback() external payable {}

