contracts/lib/Verifiers.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.13;

import { OrderStatus } from "./ConsiderationStructs.sol";

import { Assertions } from "./Assertions.sol";

import { SignatureVerification } from "./SignatureVerification.sol";

/**
 * @title Verifiers
 * @author 0age
 * @notice Verifiers contains functions for performing verifications.
 */
contract Verifiers is Assertions, SignatureVerification {
    /**
     * @dev Derive and set hashes, reference chainId, and associated domain
     *      separator during deployment.
     *
     * @param conduitController A contract that deploys conduits, or proxies
     *                          that may optionally be used to transfer approved
     *                          ERC20/721/1155 tokens.
     */
    constructor(address conduitController) Assertions(conduitController) {}

    /**
     * @dev Internal view function to ensure that the current time falls within
     *      an order's valid timespan.
     *
     * @param startTime       The time at which the order becomes active.
     * @param endTime         The time at which the order becomes inactive.
     * @param revertOnInvalid A boolean indicating whether to revert if the
     *                        order is not active.
     *
     * @return valid A boolean indicating whether the order is active.
     */
    function _verifyTime(
        uint256 startTime,
        uint256 endTime,
        bool revertOnInvalid
    ) internal view returns (bool valid) {
        // Revert if order's timespan hasn't started yet or has already ended.
        if (startTime > block.timestamp || endTime <= block.timestamp) {
            // Only revert if revertOnInvalid has been supplied as true.
            if (revertOnInvalid) {
                revert InvalidTime();
            }

            // Return false as the order is invalid.
            return false;
        }

        // Return true as the order time is valid.
        valid = true;
    }

    /**
     * @dev Internal view function to verify the signature of an order. An
     *      ERC-1271 fallback will be attempted if either the signature length
     *      is not 32 or 33 bytes or if the recovered signer does not match the
     *      supplied offerer. Note that in cases where a 32 or 33 byte signature
     *      is supplied, only standard ECDSA signatures that recover to a
     *      non-zero address are supported.
     *
     * @param offerer   The offerer for the order.
     * @param orderHash The order hash.
     * @param signature A signature from the offerer indicating that the order
     *                  has been approved.
     */
    function _verifySignature(
        address offerer,
        bytes32 orderHash,
        bytes memory signature
    ) internal view {
        // Skip signature verification if the offerer is the caller.
        if (offerer == msg.sender) {
            return;
        }

        // Derive EIP-712 digest using the domain separator and the order hash.
        bytes32 digest = _deriveEIP712Digest(_domainSeparator(), orderHash);

        // Ensure that the signature for the digest is valid for the offerer.
        _assertValidSignature(offerer, digest, signature);
    }

    /**
     * @dev Internal view function to validate that a given order is fillable
     *      and not cancelled based on the order status.
     *
     * @param orderHash       The order hash.
     * @param orderStatus     The status of the order, including whether it has
     *                        been cancelled and the fraction filled.
     * @param onlyAllowUnused A boolean flag indicating whether partial fills
     *                        are supported by the calling function.
     * @param revertOnInvalid A boolean indicating whether to revert if the
     *                        order has been cancelled or filled beyond the
     *                        allowable amount.
     *
     * @return valid A boolean indicating whether the order is valid.
     */
    function _verifyOrderStatus(
        bytes32 orderHash,
        OrderStatus storage orderStatus,
        bool onlyAllowUnused,
        bool revertOnInvalid
    ) internal view returns (bool valid) {
        // Ensure that the order has not been cancelled.
        if (orderStatus.isCancelled) {
            // Only revert if revertOnInvalid has been supplied as true.
            if (revertOnInvalid) {
                revert OrderIsCancelled(orderHash);
            }

            // Return false as the order status is invalid.
            return false;
        }

        // Read order status numerator from storage and place on stack.
        uint256 orderStatusNumerator = orderStatus.numerator;

        // If the order is not entirely unused...
        if (orderStatusNumerator != 0) {
            // ensure the order has not been partially filled when not allowed.
            if (onlyAllowUnused) {
                // Always revert on partial fills when onlyAllowUnused is true.
                revert OrderPartiallyFilled(orderHash);
            }
            // Otherwise, ensure that order has not been entirely filled.
            else if (orderStatusNumerator >= orderStatus.denominator) {
                // Only revert if revertOnInvalid has been supplied as true.
                if (revertOnInvalid) {
                    revert OrderAlreadyFilled(orderHash);
                }

                // Return false as the order status is invalid.
                return false;
            }
        }

        // Return true as the order status is valid.
        valid = true;
    }
}

contracts/lib/ZoneInteraction.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.13;

import { ZoneInterface } from "../interfaces/ZoneInterface.sol";

import { OrderType } from "./ConsiderationEnums.sol";

// prettier-ignore
import { AdvancedOrder, CriteriaResolver } from "./ConsiderationStructs.sol";

import "./ConsiderationConstants.sol";

// prettier-ignore
import {
    ZoneInteractionErrors
} from "../interfaces/ZoneInteractionErrors.sol";

import { LowLevelHelpers } from "./LowLevelHelpers.sol";

/**
 * @title ZoneInteraction
 * @author 0age
 * @notice ZoneInteraction contains logic related to interacting with zones.
 */
contract ZoneInteraction is ZoneInteractionErrors, LowLevelHelpers {
    /**
     * @dev Internal view function to determine if an order has a restricted
     *      order type and, if so, to ensure that either the offerer or the zone
     *      are the fulfiller or that a staticcall to `isValidOrder` on the zone
     *      returns a magic value indicating that the order is currently valid.
     *
     * @param orderHash The hash of the order.
     * @param zoneHash  The hash to provide upon calling the zone.
     * @param orderType The type of the order.
     * @param offerer   The offerer in question.
     * @param zone      The zone in question.
     */
    function _assertRestrictedBasicOrderValidity(
        bytes32 orderHash,
        bytes32 zoneHash,
        OrderType orderType,
        address offerer,
        address zone
    ) internal view {
        // Order type 2-3 require zone or offerer be caller or zone to approve.
        if (
            uint256(orderType) > 1 &&
            msg.sender != zone &&
            msg.sender != offerer
        ) {
            // Perform minimal staticcall to the zone.
            _callIsValidOrder(zone, orderHash, offerer, zoneHash);
        }
    }

    function _callIsValidOrder(
        address zone,
        bytes32 orderHash,
        address offerer,
        bytes32 zoneHash
    ) internal view {
        // Perform minimal staticcall to the zone.
        bool success = _staticcall(
            zone,
            abi.encodeWithSelector(
                ZoneInterface.isValidOrder.selector,
                orderHash,
                msg.sender,
                offerer,
                zoneHash
            )
        );

        // Ensure call was successful and returned the correct magic value.
        _assertIsValidOrderStaticcallSuccess(success, orderHash);
    }

    /**
     * @dev Internal view function to determine whether an order is a restricted
     *      order and, if so, to ensure that it was either submitted by the
     *      offerer or the zone for the order, or that the zone returns the
     *      expected magic value upon performing a staticcall to `isValidOrder`
     *      or `isValidOrderIncludingExtraData` depending on whether the order
     *      fulfillment specifies extra data or criteria resolvers.
     *
     * @param advancedOrder     The advanced order in question.
     * @param criteriaResolvers An array where each element contains a reference
     *                          to a specific offer or consideration, a token
     *                          identifier, and a proof that the supplied token
     *                          identifier is contained in the order's merkle
     *                          root. Note that a criteria of zero indicates
     *                          that any (transferable) token identifier is
     *                          valid and that no proof needs to be supplied.
     * @param priorOrderHashes  The order hashes of each order supplied prior to
     *                          the current order as part of a "match" variety
     *                          of order fulfillment (e.g. this array will be
     *                          empty for single or "fulfill available").
     * @param orderHash         The hash of the order.
     * @param zoneHash          The hash to provide upon calling the zone.
     * @param orderType         The type of the order.
     * @param offerer           The offerer in question.
     * @param zone              The zone in question.
     */
    function _assertRestrictedAdvancedOrderValidity(
        AdvancedOrder memory advancedOrder,
        CriteriaResolver[] memory criteriaResolvers,
        bytes32[] memory priorOrderHashes,
        bytes32 orderHash,
        bytes32 zoneHash,
        OrderType orderType,
        address offerer,
        address zone
    ) internal view {
        // Order type 2-3 require zone or offerer be caller or zone to approve.
        if (
            uint256(orderType) > 1 &&
            msg.sender != zone &&
            msg.sender != offerer
        ) {
            // If no extraData or criteria resolvers are supplied...
            if (
                advancedOrder.extraData.length == 0 &&
                criteriaResolvers.length == 0
            ) {
                // Perform minimal staticcall to the zone.
                _callIsValidOrder(zone, orderHash, offerer, zoneHash);
            } else {
                // Otherwise, extra data or criteria resolvers were supplied; in
                // that event, perform a more verbose staticcall to the zone.
                bool success = _staticcall(
                    zone,
                    abi.encodeWithSelector(
                        ZoneInterface.isValidOrderIncludingExtraData.selector,
                        orderHash,
                        msg.sender,
                        advancedOrder,
                        priorOrderHashes,
                        criteriaResolvers
                    )
                );

                // Ensure call was successful and returned correct magic value.
                _assertIsValidOrderStaticcallSuccess(success, orderHash);
            }
        }
    }

    /**
     * @dev Internal view function to ensure that a staticcall to `isValidOrder`
     *      or `isValidOrderIncludingExtraData` as part of validating a
     *      restricted order that was not submitted by the named offerer or zone
     *      was successful and returned the required magic value.
     *
     * @param success   A boolean indicating the status of the staticcall.
     * @param orderHash The order hash of the order in question.
     */
    function _assertIsValidOrderStaticcallSuccess(
        bool success,
        bytes32 orderHash
    ) internal view {
        // If the call failed...
        if (!success) {
            // Revert and pass reason along if one was returned.
            _revertWithReasonIfOneIsReturned();

            // Otherwise, revert with a generic error message.
            revert InvalidRestrictedOrder(orderHash);
        }

        // Ensure result was extracted and matches isValidOrder magic value.
        if (_doesNotMatchMagic(ZoneInterface.isValidOrder.selector)) {
            revert InvalidRestrictedOrder(orderHash);
        }
    }
}

contracts/loan/LoanAuction.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.7;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import { AmountDeriver } from "../lib/AmountDeriver.sol";
import "../PeriFiAdmin.sol";
import "./LoanAuctionEnums.sol";
import "./LoanAuctionEventsAndErrors.sol";
import "./LoanAuctionStructs.sol";
import "./LoanRepaymentCalculator.sol";
import "../vault/Vault.sol";
import "../access/MWOwnable.sol";
import "hardhat/console.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

contract LoanAuction is
    ReentrancyGuard,
    AmountDeriver,
    LoanAuctionEventsAndErrors,
    UUPSUpgradeable,
    MWOwnable
{
    uint256 public auctionCount;
    mapping(uint256 => Auction) public auctions;
    mapping(uint256 => AuctionStatus) public status;
    mapping(uint256 => uint256) public bidAmounts;
    mapping(uint256 => address) public bidders;
    address public periFiAdminAddr;
    address public loanRepaymentAddr;
    uint256 public decrementInterval;

    function initialize(
        address _periFiAdminAddr,
        address _loanRepaymentAddr
    ) public virtual initializer {
        periFiAdminAddr = _periFiAdminAddr;
        loanRepaymentAddr = _loanRepaymentAddr;
        decrementInterval = 1 days;

        __MWOwnable_init();
    }

    // display current price

    // display price for borrower

    function beginAuction(
        AuctionParameters calldata params
    ) external virtual onlyOwner nonReentrant {
        Auction memory auction = Auction({
            id: auctionCount,
            protectedUntil: 0,
            parameters: params
        });

        address owner = IERC721(params.tokenAddress).ownerOf(params.tokenId);

        // We will assume auction contract will hold the defaulted collateral for ease of use.
        if (owner != address(this)) {
            revert CollateralOwnershipNotTransferred();
        }

        if (params.startAmount <= params.endAmount) {
            revert InvalidPriceRange(params.startAmount, params.endAmount);
        }

        if (params.startTime >= params.endTime) {
            revert InvalidTimeRange(params.startTime, params.endTime);
        }

        if (block.timestamp >= params.endTime) {
            revert InvalidTimeRange(params.startTime, params.endTime);
        }

        if (params.stepDownConfig.length != 0) {
            validateStepDownConfig(
                params.startTime,
                params.endTime,
                params.stepDownConfig
            );
        }

        // set liquidation protection expiration
        if (params.isProtected) {
            uint256 protection = PeriFiAdmin(periFiAdminAddr)
                .liquidateProtectionDuration();
            auction.protectedUntil = params.startTime + protection;
        }

        auctions[auctionCount] = auction;
        status[auctionCount] = AuctionStatus.Registered;
        auctionCount += 1;
        emit LoanAuctionBegun(
            auction.id,
            params.loanId,
            params.tokenAddress,
            params.paymentToken,
            params.loanBorrower,
            params.tokenId,
            params.startAmount,
            params.endAmount,
            params.startTime,
            params.endTime,
            auction.protectedUntil,
            params.loanRepaymentAmount,
            params.stepDownConfig
        );
    }

    function makeBid(
        uint256 auctionId
    ) external virtual nonReentrant returns (bool completed) {
        if (status[auctionId] != AuctionStatus.Registered) {
            revert AuctionInvalidStatus(auctionId, status[auctionId]);
        }

        uint256 currentPrice = currentBidPrice(auctionId);

        Auction memory item = auctions[auctionId];
        AuctionParameters memory params = item.parameters;

        if (item.protectedUntil > block.timestamp) {
            // hold the asset until the protection period is over
            IERC20(params.paymentToken).transferFrom(
                msg.sender,
                address(this),
                currentPrice
            );
            bidders[auctionId] = msg.sender;
            status[auctionId] = AuctionStatus.Protected;
            bidAmounts[auctionId] = currentPrice;
            emit LoanAuctionSoldAndProtected(
                auctionId,
                msg.sender,
                params.loanBorrower,
                currentPrice
            );
            return false;
        }

        IERC721(params.tokenAddress).transferFrom(
            address(this),
            msg.sender,
            params.tokenId
        );

        // transfer erc20 from this contract to all the recipients
        LoanRepaymentCalculator calculator = LoanRepaymentCalculator(
            loanRepaymentAddr
        );
        Repayment[] memory repayments = calculator.getDestinations(
            params.loanId,
            params.loanRepaymentAmount,
            params.loanAmount,
            params.lender
        );
        address insuranceVaultAddr = calculator.insuranceVaultAddress();
        IERC20 erc20 = IERC20(params.paymentToken);
        uint256 bidAmountLeft = currentPrice;
        uint256 insuranceLeft = erc20.balanceOf(insuranceVaultAddr);
        uint repaymentLength = repayments.length;
        for (uint idx = 0; idx < repaymentLength; idx++) {
            Repayment memory repaymentItem = repayments[idx];
            uint256 paymentLeft = repaymentItem.value;

            // cover the repayment from deposit amount from bid
            if (bidAmountLeft > 0) {
                if (bidAmountLeft >= paymentLeft) {
                    console.log(
                        "sending from %s to %s, %s",
                        msg.sender,
                        repaymentItem.targetAddress,
                        paymentLeft
                    );
                    erc20.transferFrom(
                        msg.sender,
                        repaymentItem.targetAddress,
                        paymentLeft
                    );
                    bidAmountLeft -= paymentLeft;
                    continue;
                }
                console.log(
                    "sending partial from %s to %s, %s",
                    msg.sender,
                    repaymentItem.targetAddress,
                    bidAmountLeft
                );
                erc20.transferFrom(
                    msg.sender,
                    repaymentItem.targetAddress,
                    bidAmountLeft
                );
                paymentLeft -= bidAmountLeft;
                bidAmountLeft = 0;
            }
            if (insuranceVaultAddr == repaymentItem.targetAddress) {
                continue;
            }

            // if not guaranteed, next
            if (!repaymentItem.guaranteed) {
                continue;
            }
            // if guaranteed, pay with insurance
            if (insuranceLeft >= paymentLeft) {
                console.log(
                    "sending from IV to %s, %s",
                    repaymentItem.targetAddress,
                    paymentLeft
                );

                Vault(payable(insuranceVaultAddr)).transferERC20(
                    params.paymentToken,
                    repaymentItem.targetAddress,
                    paymentLeft
                );
                insuranceLeft -= paymentLeft;
                continue;
            }

            // if insurance can't cover the current payment, pay what it has, and stop
            if (insuranceLeft > 0) {
                console.log(
                    "sending partial from IV to %s, %s",
                    repaymentItem.targetAddress,
                    insuranceLeft
                );
                Vault(payable(insuranceVaultAddr)).transferERC20(
                    params.paymentToken,
                    repaymentItem.targetAddress,
                    insuranceLeft
                );
            }
            break;
        }
        if (bidAmountLeft > 0) {
            console.log(
                "sending from %s to %s, %s",
                msg.sender,
                params.loanBorrower,
                bidAmountLeft
            );
            erc20.transferFrom(msg.sender, params.loanBorrower, bidAmountLeft);
        }
        emit LoanAuctionSoldToBidder(auctionId, msg.sender, currentPrice);
        status[auctionId] = AuctionStatus.SoldToBidder;
        return true;
    }

    function claim(uint256 auctionId) external virtual nonReentrant {
        if (status[auctionId] != AuctionStatus.Protected) {
            revert AuctionInvalidStatus(auctionId, status[auctionId]);
        }

        Auction memory item = auctions[auctionId];

        AuctionParameters memory params = item.parameters;

        if (block.timestamp < item.protectedUntil) {
            revert ProtectionNotOver();
        }

        IERC721(params.tokenAddress).transferFrom(
            address(this),
            msg.sender,
            params.tokenId
        );
        status[auctionId] = AuctionStatus.SoldToBidder;
        uint256 bidAmount = bidAmounts[auctionId];
        address bidder = bidders[auctionId];

        // transfer erc20 from this contract to all the recipients
        LoanRepaymentCalculator calculator = LoanRepaymentCalculator(
            loanRepaymentAddr
        );
        Repayment[] memory repayments = calculator.getDestinations(
            params.loanId,
            params.loanRepaymentAmount,
            params.loanAmount,
            params.lender
        );

        address insuranceVaultAddr = calculator.insuranceVaultAddress();
        IERC20 erc20 = IERC20(params.paymentToken);
        uint256 bidAmountLeft = bidAmount;
        uint256 insuranceLeft = erc20.balanceOf(insuranceVaultAddr);
        uint repaymentLength = repayments.length;
        for (uint idx = 0; idx < repaymentLength; idx++) {
            Repayment memory repaymentItem = repayments[idx];
            uint256 paymentLeft = repaymentItem.value;

            // cover the repayment from deposit amount from bid
            if (bidAmountLeft > 0) {
                if (bidAmountLeft >= paymentLeft) {
                    console.log(
                        "sending from %s to %s, %s",
                        address(this),
                        repaymentItem.targetAddress,
                        paymentLeft
                    );
                    erc20.transfer(repaymentItem.targetAddress, paymentLeft);
                    bidAmountLeft -= paymentLeft;
                    continue;
                }
                console.log(
                    "sending partial from %s to %s, %s",
                    address(this),
                    repaymentItem.targetAddress,
                    bidAmountLeft
                );
                erc20.transfer(repaymentItem.targetAddress, bidAmountLeft);
                paymentLeft -= bidAmountLeft;
                bidAmountLeft = 0;
            }
            if (insuranceVaultAddr == repaymentItem.targetAddress) {
                continue;
            }
            // if not guaranteed, next
            if (!repaymentItem.guaranteed) {
                continue;
            }
            // if guaranteed, pay with insurance
            if (insuranceLeft >= paymentLeft) {
                console.log(
                    "sending from IV to %s, %s",
                    repaymentItem.targetAddress,
                    paymentLeft
                );
                Vault(payable(insuranceVaultAddr)).transferERC20(
                    params.paymentToken,
                    repaymentItem.targetAddress,
                    paymentLeft
                );
                insuranceLeft -= paymentLeft;
                continue;
            }

            // if insurance can't cover the current payment, pay what it has, and stop
            if (insuranceLeft > 0) {
                console.log(
                    "sending partial from IV to %s, %s",
                    repaymentItem.targetAddress,
                    insuranceLeft
                );
                Vault(payable(insuranceVaultAddr)).transferERC20(
                    params.paymentToken,
                    repaymentItem.targetAddress,
                    insuranceLeft
                );
            }
            break;
        }
        if (bidAmountLeft > 0) {
            console.log(
                "sending from %s to %s, %s",
                address(this),
                params.loanBorrower,
                bidAmountLeft
            );
            erc20.transfer(params.loanBorrower, bidAmountLeft);
        }
        emit LoanAuctionSoldToBidder(auctionId, bidder, bidAmount);
    }

    function payout(uint256 auctionId) external virtual nonReentrant {
        AuctionStatus currentStatus = status[auctionId];

        if (
            currentStatus != AuctionStatus.Registered &&
            currentStatus != AuctionStatus.Protected
        ) {
            revert AuctionInvalidStatus(auctionId, currentStatus);
        }

        Auction memory item = auctions[auctionId];
        AuctionParameters memory params = item.parameters;

        if (msg.sender != params.loanBorrower) {
            revert NotAuthorized();
        }

        // check auction's protection period has been expired
        if (block.timestamp > item.protectedUntil) {
            revert ProtectionOver();
        }

        uint256 repayment = params.loanRepaymentAmount;
        uint256 penalty = 0;
        if (currentStatus == AuctionStatus.Protected) {
            uint bidAmount = bidAmounts[auctionId];
            penalty = (bidAmount * 5) / 100;
            IERC20(params.paymentToken).transferFrom(
                msg.sender,
                bidders[auctionId],
                penalty
            );
            IERC20(params.paymentToken).transfer(bidders[auctionId], bidAmount);
            bidAmounts[auctionId] = 0;
        }
        uint256 fullRepayment = repayment + penalty;
        IERC721(params.tokenAddress).transferFrom(
            address(this),
            msg.sender,
            params.tokenId
        );

        LoanRepaymentCalculator calculator = LoanRepaymentCalculator(
            loanRepaymentAddr
        );
        Repayment[] memory repayments = calculator.getDestinations(
            params.loanId,
            params.loanRepaymentAmount,
            params.loanAmount,
            params.lender
        );
        IERC20 erc20 = IERC20(params.paymentToken);
        uint repaymentLength = repayments.length;
        for (uint idx = 0; idx < repaymentLength; idx++) {
            Repayment memory repaymentItem = repayments[idx];
            erc20.transferFrom(
                msg.sender,
                repaymentItem.targetAddress,
                repaymentItem.value
            );
        }
        status[auctionId] = AuctionStatus.SoldToBorrower;
        emit LoanAuctionCancelledByPayout(auctionId, msg.sender, fullRepayment);
    }

    function currentDebt(uint256 auctionId) public virtual view returns (uint256 debt) {
        AuctionStatus currentStatus = status[auctionId];

        if (
            currentStatus != AuctionStatus.Registered &&
            currentStatus != AuctionStatus.Protected
        ) {
            return 0;
        }

        Auction memory item = auctions[auctionId];
        AuctionParameters memory params = item.parameters;

        if (msg.sender != params.loanBorrower) {
            return 0;
        }

        uint256 repayment = params.loanRepaymentAmount;
        uint256 penalty = 0;
        if (currentStatus == AuctionStatus.Protected) {
            uint bidAmount = bidAmounts[auctionId];
            penalty = (bidAmount * 5) / 100;
        }
        return repayment + penalty;
    }

    function currentBidPrice(
        uint256 auctionId
    ) public view returns (uint256 amount) {
        if (status[auctionId] != AuctionStatus.Registered) {
            return 0;
        }

        Auction memory item = auctions[auctionId];
        AuctionParameters memory params = item.parameters;
        if (params.stepDownConfig.length == 0) {
            // no stepDown
            return
                _locateCurrentAmount(
                    params.startAmount,
                    params.endAmount,
                    params.startTime,
                    params.endTime,
                    true
                );
        }

        // stepDown
        return
            _locateCurrentStepDownAmount(
                params.startAmount,
                params.endAmount,
                params.startTime,
                params.stepDownConfig,
                true
            );
    }

    function validateStepDownConfig(
        uint256 startTime,
        uint256 endTime,
        bytes memory config
    ) internal virtual view {
        if (config.length <= 0) {
            revert InvalidStepDownConfig();
        }

        uint accDecrement;
        uint8 actualLength;
        uint length = config.length;
        for (uint idx = 0; idx < length; idx++) {
            uint8 decrement = uint8(config[idx]);
            if (decrement == 255) {
                break;
            }
            accDecrement += decrement;
            actualLength++;
        }

        uint256 duration = endTime - startTime;
        uint256 minDuration = uint256(actualLength * decrementInterval);
        uint256 maxDuration = uint256((actualLength + 2) * decrementInterval);
        if (duration <= minDuration) {
            revert StepDownConfigAndAuctionTimeMismatch();
        }
        if (duration >= maxDuration) {
            revert StepDownConfigAndAuctionTimeMismatch();
        }
    }

    function updateDecrementInterval(uint256 intervalInSec) public {
        require(intervalInSec != 0, "interval must be non-zero value");
        decrementInterval = intervalInSec;
    }

    function _locateCurrentStepDownAmount(
        uint256 startAmount,
        uint256 endAmount,
        uint256 startTime,
        bytes memory stepDownConfig,
        bool roundUp
    ) internal virtual view returns (uint256 amount) {
        // Only modify end amount if it doesn't already equal start amount.
        if (startAmount != endAmount) {
            // Declare variables to derive in the subsequent unchecked scope.
            uint256 duration;
            uint256 elapsed;
            uint256 remaining;

            uint256 elapsedTs;
            uint accDecrement;
            uint totalDecrement;
            // Skip underflow checks as startTime <= block.timestamp < endTime.
            unchecked {
                // Derive the duration for the order and place it on the stack.
                // duration = endTime - startTime;

                // Derive time elapsed since the order started & place on stack.
                elapsedTs = block.timestamp - startTime;

                // Derive time remaining until order expires and place on stack.
                // remaining = duration - elapsed;
            }
            uint256 daysPassed = elapsedTs / decrementInterval;
            uint length = stepDownConfig.length;
            for (uint idx = 0; idx < length; idx++) {
                uint8 decrement = uint8(stepDownConfig[idx]);
                if (decrement == 255) {
                    break;
                }
                if (idx < daysPassed) {
                    accDecrement += decrement;
                }
                totalDecrement += decrement;
            }
            duration = totalDecrement;
            elapsed = accDecrement;
            remaining = duration - elapsed;
            // Aggregate new amounts weighted by time with rounding factor.
            uint256 totalBeforeDivision = ((startAmount * remaining) +
                (endAmount * elapsed));

            // Use assembly to combine operations and skip divide-by-zero check.
            assembly {
                // Multiply by iszero(iszero(totalBeforeDivision)) to ensure
                // amount is set to zero if totalBeforeDivision is zero,
                // as intermediate overflow can occur if it is zero.
                amount := mul(
                    iszero(iszero(totalBeforeDivision)),
                    // Subtract 1 from the numerator and add 1 to the result if
                    // roundUp is true to get the proper rounding direction.
                    // Division is performed with no zero check as duration
                    // cannot be zero as long as startTime < endTime.
                    add(
                        div(sub(totalBeforeDivision, roundUp), duration),
                        roundUp
                    )
                )
            }

            // Return the current amount.
            return amount;
        }

        // Return the original amount as startAmount == endAmount.
        return endAmount;
    }

    function _authorizeUpgrade(address) internal virtual override onlyOwner {}
}

