contracts/loan/TrustDeed.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Burnable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "../access/SimpleRoleAccess.sol";

contract TrustDeed is ERC721, ERC721URIStorage, ERC721Burnable, SimpleRoleAccess {
    constructor() ERC721("PeriFi Loan Trust Deed", "PTD") {}

    function safeMint(address to, uint256 tokenId) public onlyRole("minter") {
        _safeMint(to, tokenId);
    }

    function safeBurn(uint256 tokenId) external onlyRole("minter") {
        if (_exists(tokenId)) {
            _burn(tokenId);
        }
    }

    function setTokenURI(uint256 tokenId, string memory uri) external {
        super._setTokenURI(tokenId, uri);
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    function _burn(uint256 tokenId) internal override(ERC721, ERC721URIStorage) {
        super._burn(tokenId);
    }
}

