pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "../token/ERC20/NENDCore.sol";

contract NENDBridge is Ownable {
    event EnterBridge(
        uint256 sourceChainId,
        uint256 targetChainId,
        address requester,
        uint256 amount,
        uint256 nonce
    );

    event LeaveBridge(
        uint256 sourceChainId,
        uint256 targetChainId,
        address requester,
        uint256 amount,
        uint256 nonce
    );

    modifier chainSupported(uint256 chainId) {
        require(_isChainSupported(chainId), "Chain not supported");
        _;
    }

    address token;
    mapping(uint256 => mapping(uint256 => bool))
        private chainNonceToExecutedMapping;
    uint256 private nonce;

    uint256[] public supportedChainIds;

    constructor(address _token, uint256[] memory _supportedChainIds) {
        require(
            _supportedChainIds.length > 0,
            "Must have at least one destination chain"
        );
        supportedChainIds = _supportedChainIds;
        require(
            !_isChainSupported(block.chainid),
            "The hosted chain can not be one the the supported chains"
        );
        token = _token;
    }

    function setToken(address _token) external onlyOwner {
        token = _token;
    }

    function enterBridge(uint256 _targetChainId, uint256 _amount)
        external
        chainSupported(_targetChainId)
    {
        require(_amount > 0, "Invalid amount");

        NENDCore(token).burnFrom(msg.sender, _amount);

        emit EnterBridge(
            block.chainid,
            _targetChainId,
            msg.sender,
            _amount,
            nonce++
        );
    }

    function leaveBridge(
        uint256 _sourceChainId,
        uint256 _targetChainId,
        address _requester,
        uint256 _amount,
        uint256 _nonce
    ) external onlyOwner {
        require(
            block.chainid == _targetChainId,
            "Transfer not meant for this chain"
        );

        // Duplicate request, already left bridge
        if (chainNonceToExecutedMapping[_sourceChainId][_nonce]) {
            return;
        }
        chainNonceToExecutedMapping[_sourceChainId][_nonce] = true;

        NENDCore(token).mint(_requester, _amount);

        emit LeaveBridge(
            _sourceChainId,
            _targetChainId,
            msg.sender,
            _amount,
            _nonce
        );
    }

    function _isChainSupported(uint256 chainId) internal view returns (bool) {
        for (uint256 i = 0; i < supportedChainIds.length; i++) {
            if (supportedChainIds[i] == chainId) {
                return true;
            }
        }

        return false;
    }
}
