pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../../bridge/NENDBridge.sol";

contract NENDCore is ERC20Burnable, Ownable {
    uint256 public ecosystemFund;
    uint256 public liquidity;
    uint256 public airdrop;
    uint256 public marketing;
    uint256 public team;
    uint256 public advisor;
    uint256 public sale;
    uint256 public lastInflated;
    uint256 public inflationInterval = 1 weeks;
    uint256 public inflationAmount = 100 ether;
    bool public isMintChain;
    NENDBridge bridge;

    constructor(bool _isMintChain) ERC20("NENDTEST", "N") {
        isMintChain = _isMintChain;
        if (isMintChain) {
            ecosystemFund = 25000000 ether;
            liquidity = 10000000 ether;
            airdrop = 5000000 ether;
            marketing = 5000000 ether;
            team = 10000000 ether;
            advisor = 5000000 ether;
            sale = 10000000 ether;
            _mint(
                address(this),
                ecosystemFund +
                    liquidity +
                    airdrop +
                    marketing +
                    team +
                    advisor +
                    sale
            );
        }
    }

    modifier onlyBridge() {
        require(address(bridge) != address(0x0), "Bridge not set");
        require(msg.sender == address(bridge), "Only bridge can mint");
        _;
    }

    function setBridge(NENDBridge _bridge) external onlyOwner {
        bridge = _bridge;
    }

    function mint(address _account, uint256 _amount) external onlyBridge {
        _mint(_account, _amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        _tryInflate();
        super._transfer(from, to, amount);
    }

    function _tryInflate() internal {
        if (
            isMintChain &&
            block.timestamp - lastInflated > inflationInterval &&
            totalSupply() + inflationAmount < 100000000 ether
        ) {
            lastInflated = block.timestamp;
            _mint(address(this), 100 ether);
        }
    }
}
