contracts/token/ERC20/NEND.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "../../bridge/NENDBridge.sol";
import "../../inflation/Inflation.sol";
import "./NENDAirdrop.sol";
import "./NENDCrowdSale.sol";

contract NEND is NENDAirdrop, NENDCrowdSale, NENDBridge, Inflation {
    uint256 public ecosystemFund;
    uint256 public liquidity;
    uint256 public marketing;
    uint256 public team;
    uint256 public advisor;
    bool public isMintChain;

    constructor(bool _isMainChain, uint256[] memory chains)
        ERC20("NEND", "N")
        NENDBridge(chains)
    {
        isMintChain = _isMainChain;
        if (isMintChain) {
            ecosystemFund = 25000000 ether;
            liquidity = 10000000 ether;
            airdrop = 5000000 ether;
            marketing = 5000000 ether;
            team = 10000000 ether;
            advisor = 5000000 ether;
            sale = 10000000 ether;
            _mint(
                address(this),
                ecosystemFund +
                    liquidity +
                    airdrop +
                    marketing +
                    team +
                    advisor +
                    sale
            );
        }
    }
}

contracts/inflation/Inflation.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../staking/Staking.sol";

abstract contract Inflation is ERC20, Ownable {
    uint48 public lastInflation;
    uint48 public inflationInterval = 1 weeks;
    address public staking;

    function setStaking(address _staking) external onlyOwner {
        staking = _staking;
    }

    function inflate(uint256 _amount) external onlyOwner {
        require(_amount > 0, "Invalid amount");
        require(
            lastInflation + inflationInterval < block.timestamp,
            "Too soon to inflate"
        );
        require(staking != address(0), "Staking address not set");

        address[] memory stakeTokens = Staking(staking).getStakeTokens();

        if (stakeTokens.length == 0) {
            return;
        }

        lastInflation = uint48(block.timestamp);

        address[] memory tokens = new address[](stakeTokens.length + 1);
        uint256[] memory amounts = new uint256[](stakeTokens.length + 1);

        for (uint256 i = 0; i < stakeTokens.length; i++) {
            address stakeToken = stakeTokens[i];
            tokens[i] = stakeToken;
            bool isNativeCoin = stakeToken == address(0);

            // Trade and loan commissions
            uint256 amount;

            if (isNativeCoin) {
                amount = address(this).balance;
            } else {
                amount = IERC20(stakeToken).balanceOf(address(this));
            }

            if (amount > 0) {
                if (isNativeCoin) {
                    payable(staking).transfer(amount);
                } else {
                    IERC20(stakeToken).transfer(staking, amount);
                }
                amounts[i] = amount;
            }
        }

        // Inflation rewards
        tokens[stakeTokens.length] = address(this);
        transfer(staking, _amount);
        amounts[stakeTokens.length] = _amount;

        Staking(staking).setEpochRewards(tokens, amounts);
    }
}

contracts/token/ERC20/NENDAirdrop.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

abstract contract NENDAirdrop is ERC20 {

    uint airdrop;

    event AirdropCreated(address indexed addresses, uint256 amount);

    function createAirdrop(
        address _receiver,
        uint256 _amount // onlyOwner
    ) external {
        require(airdrop >= _amount, "Not enough airdrop funds");

        airdrop -= _amount;

        _transfer(address(this), _receiver, _amount);
        emit AirdropCreated(_receiver, _amount);
    }

    function createAirdropBatch(address[] memory _receivers, uint256 _amount)
        external
    // onlyOwner
    {
        require(
            airdrop > _receivers.length * _amount,
            "Not enough airdrop funds"
        );

        airdrop -= _receivers.length * _amount;

        for (uint256 i = 0; i < _receivers.length; i++) {
            _transfer(address(this), _receivers[i], _amount);
            emit AirdropCreated(_receivers[i], _amount);
        }
    }
}

