// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

abstract contract NENDCrowdSale is ERC20 {

    event SaleCreated(address indexed buyer, uint256 amount);

    uint256 public constant tokenExchangeRate = 5000; // 5000 NEND tokens per 1 ETH
    uint256 public sale;
    bool public onSale;

    function startSale() external // onlyOwner
    {
        require(!onSale, "Already on sale");
        require(sale > 0, "Not enough sale funds");
        onSale = true;
    }

    function endSale() external // onlyOwner
    {
        require(onSale, "Sale not started");
        onSale = false;
    }

    function buyNEND() external payable {
        require(onSale, "Not on sale");

        uint256 nendsBought = msg.value * tokenExchangeRate;

        require(sale >= nendsBought, "Not enough sale funds");

        sale -= nendsBought;

        _transfer(address(this), msg.sender, nendsBought);

        emit SaleCreated(msg.sender, nendsBought);
    }
}

contracts/staking/Staking.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./IStaking.sol";

contract Staking is IStaking, ERC721, Ownable {
    address public nend;

    mapping(address => bool) internal activeStakeTokens;
    mapping(address => bool) internal addedStakeTokens;
    mapping(address => uint256) internal totalAmountStaked;
    mapping(address => uint256) internal totalRewardAllocation;
    address[] internal stakeTokens;
    uint256 internal stakeTokenCount;
    mapping(address => uint256) internal stakeCount;
    mapping(address => uint256) internal escrowCount;

    // Staker address => total inflation rewards (aggregated from all stakes)
    mapping(address => uint256) internal epochInflationRewards;

    // Token address => epoch rewards
    // address(0) = Native coin of chain
    mapping(address => uint256) internal epochStakeRewards;

    Stake[] internal stakes;

    Escrow[] internal escrows;

    uint256[3] public stakeDurations = [1 weeks, 4 weeks, 12 weeks];
    uint256[3] public rewardAllocations = [25, 35, 40];

    constructor(address _nend) ERC721("Escrowed Verification Note", "EVN") {
        nend = _nend;
    }

    function setNend(address _nend) external onlyOwner {
        nend = _nend;
    }

    function deposit(
        address _stakeToken,
        uint256 _amount,
        uint8 _durationId
    ) external payable {
        require(_amount > 0, "Zero deposit");
        require(activeStakeTokens[_stakeToken], "Invalid stake token");
        bool isNativeCoin = _stakeToken == address(0);

        if (isNativeCoin) {
            require(
                msg.sender.balance >= _amount,
                "Can't stake more than you own"
            );
        } else {
            require(
                IERC20(_stakeToken).balanceOf(msg.sender) >= _amount,
                "Can't stake more than you own"
            );
        }

        require(
            _durationId >= 0 && _durationId < stakeDurations.length,
            "Invalid stake duration"
        );

        stakes.push(
            Stake(
                stakes.length,
                msg.sender,
                _stakeToken,
                _amount,
                block.timestamp,
                0,
                _durationId
            )
        );

        if (isNativeCoin) {
            require(msg.value == _amount, "Incorrect native coin stake amount");
        } else {
            IERC20(_stakeToken).transferFrom(
                msg.sender,
                address(this),
                _amount
            );
        }

        totalAmountStaked[_stakeToken] += _amount;
        totalRewardAllocation[_stakeToken] += rewardAllocations[_durationId];
        stakeCount[msg.sender]++;

        emit Staked(msg.sender, _amount, _durationId);
    }

    function setEpochRewards(
        address[] calldata _tokens,
        uint256[] calldata _amounts
    ) external payable {
        require(msg.sender == nend, "Unauthorized");
        require(
            _tokens.length == _amounts.length,
            "Token and amount length not matching"
        );

        for (uint256 i = 0; i < _tokens.length; i++) {
            address token = _tokens[i];
            require(activeStakeTokens[token] || token == nend, "Invalid token");
            epochStakeRewards[token] = _amounts[i];
        }
    }

    function distributeRewards() external {
        for (uint256 i = 0; i < stakes.length; i++) {
            Stake storage stake = stakes[i];

            // Stake withdrawed
            if (stake.staker == address(0)) {
                continue;
            }

            uint256 depositPortion = (stake.deposited * 10000) /
                totalAmountStaked[stake.stakeToken];
            uint256 rewardAllocationPortion = (rewardAllocations[
                stake.durationId
            ] * 10000) / totalRewardAllocation[stake.stakeToken];
            uint256 portion = (depositPortion + rewardAllocationPortion) / 2;

            stake.unclaimedRewards +=
                (epochStakeRewards[stake.stakeToken] * portion) /
                10000;

            epochInflationRewards[stake.staker] +=
                (epochStakeRewards[nend] * portion) /
                10000;
        }

        for (uint256 i = 0; i < stakes.length; i++) {
            Stake memory stake = stakes[i];

            if (stake.staker == address(0)) {
                continue;
            }

            if (epochStakeRewards[stake.stakeToken] > 0) {
                epochStakeRewards[stake.stakeToken] = 0;
            }

            uint256 stakerInflationRewards = epochInflationRewards[
                stake.staker
            ] / stakeTokenCount;

            if (stakerInflationRewards > 0) {
                uint256 escrowId = escrows.length;
                uint256 claimAfter = block.timestamp + 30 weeks;
                escrows.push(
                    Escrow(escrowId, stakerInflationRewards, claimAfter)
                );

                _mint(stake.staker, escrowId);
                epochInflationRewards[stake.staker] = 0;

                emit EABIssued(escrowId, stakerInflationRewards, claimAfter);
            }
        }
    }

    function claimRewards(uint256 _stakeId) external {
        Stake memory stake = stakes[_stakeId];

        require(stake.staker == msg.sender, "Cannot claim another's stake");

        uint256 rewards = stake.unclaimedRewards;

        require(rewards > 0, "No rewards available");

        stake.unclaimedRewards = 0;

        bool isNativeCoin = stake.stakeToken == address(0);

        if (isNativeCoin) {
            payable(stake.staker).transfer(stake.unclaimedRewards);
        } else {
            IERC20(stake.stakeToken).transfer(msg.sender, rewards);
        }
    }

    function exchangeEAB(uint256 _escrowId) external {
        require(ownerOf(_escrowId) == msg.sender, "Not EAB owner");
        Escrow memory escrow = escrows[_escrowId];
        require(
            escrow.claimAfter <= block.timestamp,
            "Too soon to exchange EAB"
        );

        uint256 amount = escrow.amount;

        delete escrows[_escrowId];

        _burn(_escrowId);

        IERC20(nend).transfer(msg.sender, amount);

        escrowCount[msg.sender]--;

        emit EABExchanged(_escrowId, escrow.amount, escrow.claimAfter);
    }

    // Withdraw all stake and mint escrow
    function withdraw(uint256 _stakeId) external {
        Stake storage stake = stakes[_stakeId];

        require(stake.staker == msg.sender, "Cannot withdraw another's stake ");
        require(
            IERC20(stake.stakeToken).balanceOf(address(this)) > stake.deposited,
            "Insufficient pool balance"
        );

        uint256 withdrawAfter = stake.timeDeposited +
            stakeDurations[stake.durationId];

        require(block.timestamp >= withdrawAfter, "Too soon to withdraw");

        uint256 _withdrawAmount = stake.deposited;
        _withdrawAmount += stake.unclaimedRewards;

        delete stakes[_stakeId];

        IERC20(stake.stakeToken).transfer(msg.sender, _withdrawAmount);

        totalAmountStaked[stake.stakeToken] -= stake.deposited;
        totalRewardAllocation[stake.stakeToken] -= rewardAllocations[
            stake.durationId
        ];
        stakeCount[msg.sender]--;

        emit Withdrawed(msg.sender, _withdrawAmount, stake.durationId);
    }

    function getStakeTokens()
        external
        view
        returns (address[] memory _stakeTokens)
    {
        if (stakeTokenCount == 0) {
            return _stakeTokens;
        }

        uint256 idx;
        for (uint256 i = 0; i < stakeTokens.length; i++) {
            address stakeToken = stakeTokens[i];

            if (activeStakeTokens[stakeToken]) {
                _stakeTokens[idx++] = stakeToken;
            }
        }
    }

    function getStakes(address _user)
        external
        view
        returns (Stake[] memory _stakes)
    {
        uint256 _stakeCount = stakeCount[_user];
        if (_stakeCount == 0) {
            return _stakes;
        }
        _stakes = new Stake[](_stakeCount);
        uint256 idx;

        for (uint256 i = 0; i < stakes.length; i++) {
            Stake memory stake = stakes[i];

            if (stake.staker == _user) {
                _stakes[idx++] = stake;
            }
        }
    }

    function getEscrows(address _user)
        external
        view
        returns (Escrow[] memory _escrows)
    {
        uint256 _escrowCount = escrowCount[_user];
        if (_escrowCount == 0) {
            return _escrows;
        }
        _escrows = new Escrow[](_escrowCount);
        uint256 idx;

        for (uint256 i = 0; i < escrows.length; i++) {
            if (ownerOf(i + 1) == _user) {
                _escrows[idx++] = escrows[i];
            }
        }
    }

    function addStakeToken(address _stakeToken) external onlyOwner {
        if (!addedStakeTokens[_stakeToken]) {
            addedStakeTokens[_stakeToken] = true;
            stakeTokens.push(_stakeToken);
        }

        if (!activeStakeTokens[_stakeToken]) {
            stakeTokenCount++;
            activeStakeTokens[_stakeToken] = true;
        }
    }

    function removeStakeToken(address _stakeToken) external onlyOwner {
        if (!addedStakeTokens[_stakeToken]) {
            return;
        }

        if (activeStakeTokens[_stakeToken]) {
            stakeTokenCount--;
            activeStakeTokens[_stakeToken] = false;
        }
    }
}

