// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

interface IStaking {
    event Staked(address indexed staker, uint256 amount, uint8 durationId);
    event Withdrawed(address indexed staker, uint256 amount, uint8 durationId);
    event EABIssued(
        uint256 indexed escrowId,
        uint256 amount,
        uint256 claimAfter
    );
    event EABExchanged(
        uint256 indexed escrowId,
        uint256 amount,
        uint256 claimAfter
    );

    struct Stake {
        uint256 id;
        address staker;
        address stakeToken;
        // The deposited amount
        uint256 deposited;
        // The time of deposit
        uint256 timeDeposited;
        // Unclaimed rewards
        uint256 unclaimedRewards;
        // stake duration
        uint8 durationId;
    }

    struct Escrow {
        uint256 id;
        // The amount of NEND to be claimed
        uint256 amount;
        // The time after which the escrow can be exchanged for NEND
        uint256 claimAfter;
    }

    function deposit(
        address _stakeToken,
        uint256 _amount,
        uint8 _durationId
    ) external payable;

    function setEpochRewards(
        address[] calldata _tokens,
        uint256[] calldata _amounts
