contracts/token/ERC721/PeriFiNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

contract PeriFiNFT is ERC721, ERC721URIStorage, Ownable {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIdCounter;

    constructor(string memory _name, string memory _symbol)
        ERC721(_name, _symbol)
    {}

    function safeMint(address to, string memory uri) public onlyOwner {
        _tokenIdCounter.increment();
        uint256 tokenId = _tokenIdCounter.current();
        _safeMint(to, tokenId);
        _setTokenURI(tokenId, uri);
    }

    // The following functions are overrides required by Solidity.

    function _burn(uint256 tokenId)
        internal
        override(ERC721, ERC721URIStorage)
    {
        super._burn(tokenId);
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return ERC721URIStorage.tokenURI(tokenId);
    }
}

contracts/voting/PowerCard.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Burnable.sol";

contract PowerCard is ERC1155Burnable, Ownable {
    uint256 private constant MAX_UINT = 2**256 - 1;
    // 4 card levels
    uint256 public constant MAX_LEVEL = 4;
    // 5 cards needed for a card of next level
    uint256 public constant LEVEL_UP_REQUIREMENT = 5;

    event LootBoxPurchased(address indexed _buyer, uint256[4] cards);

    modifier validLevel(uint256 level) {
        require(level > 0 && level < 5, "Invalid card level");
        _;
    }

    modifier notZero(uint256 amount) {
        require(amount > 0);
        _;
    }

    uint256[4] public priceByLevel = [5 ether, 10 ether, 20 ether, 40 ether];

    uint256 public constant LOOTBOX_PRICE = 10;

    address public paymentToken;

    constructor(address _paymentToken) ERC1155("") {
        paymentToken = _paymentToken;
    }

    function setPaymentToken(address _paymentToken) external onlyOwner {
        paymentToken = _paymentToken;
    }

    function getPricePerCardByLevel(uint256 _level)
        public
        view
        validLevel(_level)
        returns (uint256)
    {
        return priceByLevel[_level - 1];
    }

    function buyCards(uint256 _level, uint256 _amount)
        external
        validLevel(_level)
        notZero(_amount)
    {
        uint256 pricePerCard = getPricePerCardByLevel(_level);
        uint256 totalPrice = pricePerCard * _amount;

        IERC20(paymentToken).transferFrom(msg.sender, address(this), totalPrice);

        _mint(msg.sender, _level, _amount, msg.data);
    }

    function buyLootBoxes(uint256 _amount)
        external
        notZero(_amount)
        returns (uint256[4] memory cards)
    {
        // Chance of getting cards by level
        // Level 1: 60%
        // Level 2: 25%
        // Level 3: 10%,
        // Level 4: 5%

        uint256 totalPrice = _amount * LOOTBOX_PRICE;

        require(IERC20(paymentToken).balanceOf(msg.sender) >= _amount * LOOTBOX_PRICE);

        IERC20(paymentToken).transferFrom(msg.sender, address(this), totalPrice);

        for (uint256 i = 0; i < _amount; i++) {
            uint256 random = uint256(
                keccak256(
                    abi.encodePacked(block.timestamp, msg.sender, _amount, i)
                )
            );
            if (random < (MAX_UINT / 100) * 60) {
                cards[0]++;
            } else if (random < (MAX_UINT / 100) * 85) {
                cards[1]++;
            } else if (random < (MAX_UINT / 100) * 95) {
                cards[2]++;
            } else {
                cards[3]++;
            }
        }

        for (uint256 i = 0; i < 4; i++) {
            _mint(msg.sender, i + 1, cards[i], msg.data);
        }

        emit LootBoxPurchased(msg.sender, cards);
    }
}

