contracts/commision/CommissionVault.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";

contract CommissionVault is Ownable {
    enum PaymentTokenStatus {
        NONE, // Token not added, default
        DISABLED, // Token added but disabled
        ENABLED // Token added and enabled
    }

    constructor(
        address _router,
        address _pool,
        address _nend
    ) {
        router = _router;
        pool = _pool;
        nend = _nend;
    }

    address public pool;
    address public router;
    address public nend;

    mapping(address => PaymentTokenStatus) public tokenStatus;

    address[] public tokens;

    function addOrEnableToken(address _address) external onlyOwner {
        if (tokenStatus[_address] != PaymentTokenStatus.NONE) {
            tokenStatus[_address] = PaymentTokenStatus.ENABLED;
        } else {
            tokenStatus[_address] = PaymentTokenStatus.ENABLED;
            tokens.push(_address);
        }
    }

    function disableToken(address _address) external onlyOwner {
        if (tokenStatus[_address] != PaymentTokenStatus.NONE) {
            tokenStatus[_address] = PaymentTokenStatus.DISABLED;
        }
    }

    function exchangeNEND() external {
        for (uint256 i = 0; i < tokens.length; i++) {
            address addr = tokens[i];

            // Token not added or disabled
            if (tokenStatus[addr] != PaymentTokenStatus.ENABLED) {
                continue;
            }

            IERC20 erc20 = IERC20(addr);
            uint256 balance = erc20.balanceOf(address(this));

            // Nothing to exchange
            if (balance == 0) {
                continue;
            }

            require(erc20.approve(router, balance), "Approve failed");

            ISwapRouter(router).exactInputSingle(
                ISwapRouter.ExactInputSingleParams(
                    addr,
                    nend,
                    3000, // Default 0.3% for all NEND pools
                    pool,
                    block.timestamp,
                    balance,
                    0,
                    0
                )
            );
        }
    }

    function setRouter(address _router) external onlyOwner {
        router = _router;
    }

    function setPool(address _pool) external onlyOwner {
        pool = _pool;
    }

    function setNend(address _nend) external onlyOwner {
        nend = _nend;
    }
}

