        int256 amount0Delta,
        int256 amount1Delta,
        bytes calldata data
    ) external;
}

contracts/voting/PeriFiCuration.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Burnable.sol";

contract PeriFiCuration is Ownable {
    struct Vote {
        uint8 cardLevel;
        uint16 options;
        uint48 voteTime;
        address voter;
    }

    event VoteCasted(
        uint8 cardLevel,
        uint16 options,
        uint48 voteTime,
        bytes32 curationHash,
        address voter
    );

    // Curation hash => votes
    mapping(bytes32 => Vote[]) public votes;

    address public voteToken;

    constructor(address _voteToken) {
        voteToken = _voteToken;
    }

    function setVoteToken(address _voteToken) external onlyOwner {
        voteToken = _voteToken;
    }

    function castVote(
        uint8 cardLevel,
        uint16 options,
        bytes32 curationHash,
        uint48 curationEnd
    ) external {
        require(block.timestamp < curationEnd, "Curation ended");
        require(cardLevel >= 0 && cardLevel < 5, "Invalid card level");
        // Options validation
        require(
            (7 & options) <= 5 &&
                ((56 & options) >> 3) <= 5 &&
                ((448 & options) >> 6) <= 5,
            "Invalid options"
        );

        // Card level 0 = No betting
        if (cardLevel > 0) {
            ERC1155Burnable(voteToken).burn(msg.sender, cardLevel, 1);
        }

        uint48 voteTime = uint48(block.timestamp);

        votes[curationHash].push(
            Vote(cardLevel, options, voteTime, msg.sender)
        );

        emit VoteCasted(cardLevel, options, voteTime, curationHash, msg.sender);
    }
}

